/// <summary>
/// The <c>TaxIntegrationRoundingConfiguration</c> class holds the tax integration configurations
/// The configuration will impact tax integration rounding strategy.
/// </summary>
public class TaxIntegrationRoundingConfiguration
{
    private TaxRoundOffCalculationMethod roundOffCalculationMethod = TaxRoundOffCalculationMethod::Line;
    private TaxRoundOffPrinciple roundOffPrinciple = TaxRoundOffPrinciple::TaxCode;
    private TaxRoundOff roundOff = 0.01;
    private TaxRoundOffType roundOffType = RoundOffType::Ordinary;

}

