/// <summary>
/// The <c>TaxIntegrationTaxFeatureSetupSyncContract</c> class will be used to hold tax setup data during synchronizing tax feature setup.
/// </summary>
internal class TaxIntegrationTaxFeatureSetupSyncContract
{
    private List taxTableList;
    private List taxDataList;
    private List taxLimitList;
    private List taxGroupDataList;
    private List taxOnItemList;

}

