/// <summary>
/// Gets the assessable value of the document line.
/// </summary>
/// <returns>
/// The assessable value.
/// </returns>
public AmountCur getAssessableValueTransactionCurrency()
{
    return purchRFQLine.getMiscChargesAmount_IN(purchRFQLine.LineAmount);
}

