/// <summary>
///    This class represents an algorithm for "Document number" numbering of Tax Report 340 records.The
///    <c>TaxReportLines.DocNumber</c> values are used as record numbers in this algorithm.
/// </summary>
public class TaxRepRecordNumbering_DocNum_ES extends TaxRepRecordNumbering_ES
{
}

Method.Source:
/// <summary>
///    Gets the first record number that is generated by the algorithm.
/// </summary>
/// <returns>
///    The first record number that is generated by the algorithm.
/// </returns>
public TaxReportRecord firstNum()
{
    return '';
}

/// <summary>
///    Gets the current Tax Report 340 line number, depending on the properties of the line.
/// </summary>
/// <param name="_taxReportLines">
///    The line for which the record number is generated.
/// </param>
/// <returns>
///    The next record number.
/// </returns>
public TaxReportRecord getNextRecordNumber(TaxReportLines _taxReportLines)
{
    return _taxReportLines.DocumentNum;
}

/// <summary>
///    The last record number that is generated by the algorithm.
/// </summary>
/// <returns>
///    The last record number that is generated by the algorithm.
/// </returns>
public TaxReportRecord lastNum()
{
    return '';
}
