{
    return TaxTransDeclarationViewHelper::deductibleTaxBase(
        SysComputedColumn::returnField(_viewName, TaxTransDeclarationDetailsViewConstants::DSTaxTrans, fieldStr(TaxTrans, TaxBaseAmountRep)), _viewName);
}

