/// <summary>
/// Determines whether Date of VAT register filling: new calculation choice feature is enabled.
/// </summary>
/// <returns>true if Date of VAT register filling: new calculation choice feature is enabled; otherwise, false.</returns>
internal static boolean isEnabled()
{
    return FeatureStateProvider::isFeatureEnabled(TaxVATDueDateCustomizeFeature::instance());
}
