/// <summary>
/// Represents the tax document bundler for normal purch invoice.
/// </summary>
[TaxableDocumentDescriptorAttribute("Purchase order invoice", "PurchInvoice")]
public class TaxableDocumentDescriptorPurchaseInvoice extends TaxableDocumentDescriptorBase
    implements TaxableDocumentTypeDefinitionPurchaseInvoice
{
}

Method.Source:
/// <summary>
/// Gets the data provider.
/// </summary>
/// <returns>
/// The data provider instance.
/// </returns>
public ITaxableDocumentDataProvider getDataProvider()
{
    return new TaxableDocPurchInvoiceDataProvider(this);
}

/// <summary>
/// Is tax adjustable for this transaction
/// </summary>
/// <returns>
/// True if tax is adjustable; otherwise, false.
/// </returns>
public boolean isTaxAdjustable()
{
    return true;
}

/// <summary>
/// Indicates if tax document should be persisted in database.
/// </summary>
/// <returns>True if the tax document should be persisted; otherwise, false.</returns>
public boolean shouldPersistTaxDocument()
{
    return true;
}
