/// <summary>
/// Gets the total balance sum with simulation.
/// </summary>
/// <param name = "_tmpDateSumCode">The <c>TmpDateSumCode</c> record.</param>
/// <returns>The total balance sum with simulation.</returns>
[SysClientCacheDataMethodAttribute(true)]
public static display Percent sumInPercentWithSim_IT(TmpDateSumCode _tmpDateSumCode)
{
    return percent(_tmpDateSumCode.TotalWithSim_IT, _tmpDateSumCode.SumInTotalWithSim_IT);
}
