using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// Represents discount amount update when payment date changed feature for integration with Feature Management.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public class UpdateDiscountWhenPaymentDateChangedFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static UpdateDiscountWhenPaymentDateChangedFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new UpdateDiscountWhenPaymentDateChangedFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>An <c>SeparateAcctForCreditNoteFeature</c> instance.</returns>
[Hookable(false)]
public static UpdateDiscountWhenPaymentDateChangedFeature instance()
{
    return UpdateDiscountWhenPaymentDateChangedFeature::instance;
}

/// <summary>
/// Determines whether feature is enabled.
/// </summary>
/// <returns>True, if feature is enabled; otherwise, false.</returns>
public static boolean isEnabled()
{
    #isoCountryRegionCodes

    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR])
        && Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureStateProvider::isFeatureEnabled(UpdateDiscountWhenPaymentDateChangedFeature::instance());
}
