using System.ComponentModel.Composition;

/// <summary>
/// The <c>VendInvoiceAutomationMatchingValidationTask</c> fulfills the contract needed for <c>ProcessAutomationTask</c>.
/// </summary>
[ExportMetadata(classStr(ProcessAutomationTask), classStr(VendInvoiceAutomationMatchingValidationTask))]
[Export(identifierStr('Microsoft.Dynamics.AX.Application.ProcessAutomationTask'))]
internal final class VendInvoiceAutomationMatchingValidationTask extends ProcessAutomationTask implements BatchRetryable
{
}

Method.Source:
private boolean isAutoMatchingValidationEnabledForAnyCompany()
{
    VendParameters vendParameters;

    select firstonly crosscompany RecId from vendParameters
        where vendParameters.AutomationProductReceiptMatching == NoYes::Yes;

    return vendParameters.RecId != 0;
}

protected List getListOfWorkToBePerformed()
{
    VendInvoiceAutomationMatchingValidationController matchingValidationController = VendInvoiceAutomationMatchingValidationController::construct();
    ProcessScheduleWorkItem executionWorkItem = this.parmProcessScheduleWorkItem();

    // Set the data contract on the automatic matching validation task to the process work item generated by Process Automation
    ProcessScheduleWorkItem processScheduleWorkItemContract = matchingValidationController.getDataContractObject() as ProcessScheduleWorkItem;
    processScheduleWorkItemContract.initializeFromScheduleWorkItem(executionWorkItem);

    List workList = new List(Types::Class);
    workList.addEnd(matchingValidationController);

    return workList;
}

/// <summary>
/// Specifies if the batch task is retryable for transient exceptions or not.
/// </summary>
/// <returns>
/// If true is returned, the batch task is retryable, otherwise it is not.
/// </returns>
[Hookable(false)]
final boolean isRetryable() 
{
    return true;
}
