public boolean validateFields()
{
    boolean    isValid = true;

    if (vendRecordType == DirPartyBaseType::Organization && !organizationName)
    {
        isValid = checkFailed(strFmt("@SYS110217", "@SYS129167"));
    }

    if (vendRecordType == DirPartyBaseType::Person
        && !personFirstName
        && !personMiddleName
        && !personLastName)
    {
        isValid = checkFailed("@SYS191220");
    }

    if (!vendGroupId)
    {
        isValid = checkFailed(strFmt("@SYS110217", "@SYP4950112"));
    }

    if (!logisticsAddressStreet)
    {
        isValid = checkFailed(strFmt("@SYS110217", "@SYS323505"));
    }

    if (bankAccountId)
    {
        if ((!bankRoutingNumber) && (!bankSWIFTNo || !bankIBAN))
        {
            isValid = checkFailed("@SYP4950120");
        }

        if ((!bankSWIFTNo) && (!bankRoutingNumber || !bankAccountNumber))
        {
            isValid = checkFailed("@SYP4950121");
        }

        if ((bankRoutingNumber != '' && !bankAccountNumber) && (!bankSWIFTNo || !bankIBAN))
        {
            isValid = checkFailed("@SYP4950120");
        }

        if ((bankSWIFTNo != '' && !bankIBAN) && (!bankRoutingNumber || !bankAccountNumber))
        {
            isValid = checkFailed("@SYP4950121");
        }
    }

    return isValid;
}
