using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>VendParametersRequiredBankIbanSwiftFeature</c> class controls if the warehouse operations use the new own inventory transactions <c>WHSInventoryTransaction</c> instead
/// of the <c>InventTrans</c> to make changes in the on-hand.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class VendParametersRequiredBankIbanSwiftFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static VendParametersRequiredBankIbanSwiftFeature singleton;

}

Method.Source:
[Hookable(false)]
public static VendParametersRequiredBankIbanSwiftFeature instance()
{
    if (!singleton)
    {
        singleton = new VendParametersRequiredBankIbanSwiftFeature();
    }

    return singleton;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    VendParameters vendParameters;

    while select forupdate crosscompany vendParameters
    {
        changecompany (vendParameters.DataAreaId)
        {
            vendParameters.VendBankIbanSwiftRequired = NoYes::Yes;
            vendParameters.update();
        }
    }

    ttscommit;
}
