protected VendPaymProposalAutomationCriteriaControls initAutomationCriteriaControls()
{
    VendPaymProposalAutomationCriteriaControls controlsLocal = next initAutomationCriteriaControls();

    controlsLocal.voucherType_CNControl = this.VoucherType_CN;
    controlsLocal.voucherTypeGroup_CNControl = this.VoucherTypeGroup_CN;

    return controlsLocal;
}
