/// <summary>
///    Retrieves the query name to use for the category extension workflow.
/// </summary>
/// <returns>
///    The query name of the <c>VendRequestCategoryNCARequest</c> query.
/// </returns>
public queryName getQueryName()
{
    queryName ret;

    ret = querystr(VendRequestCategoryNCARequest);

    return ret;
}
