using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>VendSupplyRiskFeature</c> class defines the feature management feature,
/// when enabled the supply risk assessment dashboard can be used.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
[SysObsolete("This class is obsolete, since feature is not referenced anymore", false, 24\06\2024)]
public final class VendSupplyRiskFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static VendSupplyRiskFeature singleton;

}

Method.Source:
[Hookable(false)]
public static VendSupplyRiskFeature instance()
{
    if (!singleton)
    {
        singleton = new VendSupplyRiskFeature();
    }

    return singleton;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}
