using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>VendorDispatchDateFeature</c> class defines the feature management feature,
/// when enabled the requested and confirmed ship dates on purchase order and planned purchase order can be used.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
[FeatureMetadataConfigurationKey(configurationKeyStr(LogisticsBasic))]
public final class VendorDispatchDateFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static VendorDispatchDateFeature singleton;

}

Method.Source:
[Hookable(false)]
public static VendorDispatchDateFeature instance()
{
    if (!singleton)
    {
        singleton = new VendorDispatchDateFeature();
    }

    return singleton;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    PurchParameters purchParameters;
    while select forupdate crosscompany purchParameters
    {
        changecompany (purchParameters.DataAreaId)
        {
            purchParameters.IsSupplierShipmentDatesEnabled = NoYes::Yes;
            purchParameters.doUpdate();
        }
    }

    ttscommit;
}

[Hookable(false)]
public void onDisabled()
{
    ttsbegin;

    PurchParameters purchParameters;
    while select forupdate crosscompany purchParameters
    {
        changecompany (purchParameters.DataAreaId)
        {
            purchParameters.IsSupplierShipmentDatesEnabled = NoYes::No;
            purchParameters.doUpdate();
        }
    }

    ttscommit;
}
