SourceCode.Declaration:
/// <summary>
/// The <c>WHSFeatureInfo</c> class is the data Contract class with information about a Feature/flight.
/// </summary>
[DataContractAttribute]
public class WHSFeatureInfo
{
    private SysFlightName name;
    private boolean isFlight;
    private boolean isEnabled;

}

Method.Source:
[DataMemberAttribute]
public SysFlightName name(SysFlightName _featureName = name)
{
    name = _featureName;
    return name;
}

[DataMemberAttribute]
public boolean isFlight(boolean _isFlight = isFlight)
{
    isFlight = _isFlight;
    return isFlight;
}

[DataMemberAttribute]
public boolean isEnabled(boolean _isEnabled = isEnabled)
{
    isEnabled = _isEnabled;
    return isEnabled;
}
