protected WHSMobileAppStepId stepId()
{
    WHSMobileAppStepId stepIdLocal = super();

    if (stepIdLocal == WHSMobileAppStepIds::ContainerId)
    {
        return WHSMobileAppStepIds::ContainerIdToPack;
    }

    return super();
}
