using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>WHSReleaseOnlyWhenFullReservationFeature</c> class adds the option to release production
/// orders only when a full reservation of the bill of materials for the production order can be
/// fulfilled.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[FeatureMetadataConfigurationKey(configurationKeyStr(WHSandTMS))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class WHSReleaseOnlyWhenFullReservationFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static WHSReleaseOnlyWhenFullReservationFeature singleton;

}

Method.Source:
[Hookable(false)]
public static WHSReleaseOnlyWhenFullReservationFeature instance()
{
    if (!singleton)
    {
        singleton = new WHSReleaseOnlyWhenFullReservationFeature();
    }

    return singleton;
}

[Hookable(false)]
internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}
