/// <summary>
/// Mobile Device controls supporting the flow of consolidating two license plates.
/// </summary>
public class WHSWorkExecuteDisplayConsolidateLPControls
{
    /// <summary>
    /// Control name on mobile device for showing instructions
    /// </summary>
    public static const FormControlName LicensePlateConsolidationInstructionsLabel = 'LicensePlateConsolidationInstructionsLabel';
    /// <summary>
    /// Control name on mobile device for 'Target LP' or the license plate we will consolidate to
    /// </summary>
    public static const FormControlName ConsolidateToLicensePlateId = 'ConsolidateToLicensePlateId';
    /// <summary>
    /// Control name on mobile device for 'LP to merge' or the license plate we will consolidate from.
    /// </summary>
    public static const FormControlName ConsolidateFromLicensePlateId = 'ConsolidateFromLicensePlateId';
    /// <summary>
    /// Control name on mobile device for 'LP to merge' or the license plates we will consolidate from.
    /// </summary>
    public static const FormControlName ConsolidateFromLicensePlateIds = 'ConsolidateFromLicensePlateIds';
    /// <summary>
    /// Control name on mobile device for showing the content of the 'LP to merge' for confirmation.
    /// </summary>
    public static const FormControlName LicensePlateContentLabel = 'LicensePlateContentLabel';

}
