/// <summary>
/// Mobile Device controls supporting FEFO batch labels.
/// </summary>
public class WHSWorkExecuteDisplayFEFOBatchLabelControls
{
    /// <summary>
    /// Used to show the list of oldest batches in a location.
    /// </summary>
    public static const str FEFOBatchList = 'FEFOBatchList';
    /// <summary>
    /// Used to show the list of license plates that have the oldest batch in a location.
    /// </summary>
    public static const str FEFOBatchLPList = 'FEFOBatchLPList';
    /// <summary>
    /// List of locations with the oldest batches in the warehouse.
    /// </summary>
    public static const str FEFOLocationList = 'FEFOLocationList';
    /// <summary>
    /// Button to ignore the warn and keep the current work line location.
    /// </summary>
    public static const str IgnoreLocationFEFOWarn = 'IgnoreWarn';
    /// <summary>
    /// Label to show information on location.
    /// </summary>
    public static const str LocationInformation = 'LocationInformation';

}
