/// <summary>
/// Mobile device controls used to support group pick operations.
/// </summary>
internal class WHSWorkExecuteDisplayGroupPickControls
{
    /// <summary>
    /// Control name on the mobile device for storing if Group Pick is enabled.
    /// </summary>
    internal static const FormControlName GroupPick         = 'GroupPick';

    /// <summary>
    /// Control name on the mobile device for storing the qty being picked for each group.
    /// </summary>
    internal static const FormControlName GroupPickQty      = 'GroupPickQty';

    /// <summary>
    /// Control name on the mobile device that stores the license plate for items being picked from LP controlled locations.
    /// </summary>
    internal static const FormControlName GroupPickLP       = 'GroupPickLP';

    /// <summary>
    /// Control name on the mobile device for storing if Group Pick has been confirmed.
    /// </summary>
    internal static const FormControlName GroupPickConfirm  = 'GroupPickConfirm';

    /// <summary>
    /// Control name on the mobile device for storing if Group Pick is enabled but has failed validation.
    /// </summary>
    internal static const FormControlName GroupPickFail     = 'GroupPickFail';
}
