/// <summary>
/// Mobile Device controls supporting the flow of movement.
/// </summary>
public class WHSWorkExecuteDisplayMovementControls
{
    /// <summary>
    /// Control name on mobile device for the movement title label.
    /// </summary>
    public static const FormControlName MovementLabel = 'Movement';
    /// <summary>
    /// Control name on mobile device for the to (movement) label.
    /// </summary>
    public static const FormControlName MovementToLabel = 'To';
    /// <summary>
    /// Control name on mobile device for the from (movement) label.
    /// </summary>
    public static const FormControlName MovementFromLabel = 'From';
    /// <summary>
    /// Control name on mobile device for physical inventory quantity (total available in location / license plate)
    /// </summary>
    public static const FormControlName PhysicalInventoryQty = 'PhysInvQty';
    /// <summary>
    /// Control name on mobile device for indicating that the movement refers to a container.
    /// </summary>
    public static const FormControlName ContainerMovement = 'ContainerMovement';
    /// <summary>
    /// Control name on mobile device for the contents of the container.
    /// </summary>
    public static const FormControlName ContainerContentsLabel = 'ContainerContents';
    /// <summary>
    /// Control name on mobile device for indicating that the movement refers to a container group.
    /// </summary>
    public static const FormControlName ContainerGroupMovement = 'ContainerGroupMovement';
    /// <summary>
    /// Control name on mobile device for the contents of the container group.
    /// </summary>
    public static const FormControlName ContainerGroupContentsLabel = 'ContainerGroupContents';
    /// <summary>
    /// Control name on mobile device for the identifier of the pallet merge container.
    /// </summary>
    internal static const FormControlName MergeContainerId = 'MergeContainerId';
    /// <summary>
    /// Control name on mobile device for indicating to move the container.
    /// </summary>
    internal static const FormControlName MergeMoveContainer = 'MergeMoveContainer';
}
