/// <summary>
/// Mobile Device controls supporting piece by piece confirming.
/// </summary>
public class WHSWorkExecuteDisplayPieceByPieceControls
{
    /// <summary>
    /// Control name on mobile device for piece by piece.
    /// </summary>
    public static const FormControlName PieceByPieceConfirmation = 'PieceByPieceConfirmation';

    /// <summary>
    /// Value of pieces confirmed tracked through the pass.
    /// </summary>
    public static const str PiecesConfirmed = 'PiecesConfirmed';

    /// <summary>
    /// Value of total pieces to confirm tracked through the pass.
    /// </summary>
    public static const str TotalPiecesToConfirm = 'TotalPiecesToConfirm';

    /// <summary>
    /// Container of tracked UOMs and quantities for cycle counting when using piece by piece confirming.
    /// </summary>
    public static const str CycleCountContainer = 'CycleCountContainer';

    /// <summary>
    /// The total invent quantity of the last attempt at a cycle count.
    /// </summary>
    public static const str LastCycleCount = 'LastCycleCount';

	/// <summary>
    /// The current count of confirmed quantity.
    /// </summary>
    public static const str ConfirmedPiecePickingQty = 'ConfirmedPiecePickingQty';

	/// <summary>
    /// Used to show in the pass that a barcode was used during mixed license plate receiving.
    /// </summary>
    public static const str MixedLPBarcodeUsed = 'MixedLPBarcodeUsed';

    /// <summary>
    ///  Value of unit of measure for pieces to confirm tracked through the pass.
    /// </summary>
    public static const str PieceByPieceConfirmUOM = 'PieceByPieceConfirmUOM';
}
