/// <summary>
/// Mobile Device controls supporting the flow of removing container from container group.
/// </summary>
public class WHSWorkExecuteDisplayRemoveContainerFromGroupControls
{
    /// <summary>
    /// Control name on mobile device for showing instructions.
    /// </summary>
    public static const FormControlName RemoveContainerFromGroupInstructionsLabel = 'RemoveContainerFromGroupInstructionsLabel';
    /// <summary>
    /// Control name on mobile device for Contaienr ID to be removed from a group.
    /// </summary>
    public static const FormControlName RemoveContainerId = 'RemoveContainerId';
    /// <summary>
    /// Control name on mobile device for counting the removed containers.
    /// </summary>
    public static const FormControlName RemovedContainerCounter = 'RemovedContainerCounter';
    /// <summary>
    /// Control name on mobile device for keeping track of removed container IDs.
    /// </summary>
    public static const FormControlName RemovedContainerIds = 'RemovedContainerIds';
    /// <summary>
    /// Control name on mobile device for the button to show the container IDs to be removed.
    /// </summary>
    public static const FormControlName RemovedContainerIdsButton = 'RemovedContainerIdsButton';
}
