/// <summary>
/// Mobile device controls supporting verification controls.
/// </summary>
public class WHSWorkExecuteDisplayVerificationControls
{
    /// <summary>
    /// Control name on mobile device for showing license plate verification.
    /// </summary>
    public static const FormControlName LPVerification = 'LPVerification';
    /// <summary>
    /// If work was started by scanning the LP we can skip LP verification.
    /// </summary>
    public static const FormControlName SkipLPVerification = 'SkipLPVerification';
    /// <summary>
    /// Control name on mobile device for showing batch verification.
    /// </summary>
    public static const FormControlName BatchVerification = 'BatchVerification';
    /// <summary>
    /// Control name on mobile device for showing serial verification.
    /// </summary>
    public static const FormControlName SerialVerification = 'SerialVerification';
}
