public static void main(Args args)
{
    SysConsistencyCheckJob          sysConsistencyCheckJob;

    sysConsistencyCheckJob = new SysConsistencyCheckJob(classNum(WMSConsistencyCheck));

    if (!sysConsistencyCheckJob.prompt())
    {
        return;
    }

    sysConsistencyCheckJob.runOperation();
}

