/// <summary>
/// Calculates number of cells to be filled with data
/// </summary>
/// <returns>
/// Number of cells to be filled with data
/// </returns>
#define.cellsBody(11)
#define.cellsOther(29)
protected Counter totalCells()
{
    return this.getNumOfLines() * #cellsBody + #cellsOther;
}

