public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    #isoCountryRegionCodes

    super(_entityCtx, _dataSourceCtx);

    if (_dataSourceCtx.name() == dataentitydatasourcestr(AssetValueModelEntity, AssetBook))
    {
        AssetValueModelEntity entity = _entityCtx.getEntityRecord();
        AssetBook assetBook = _dataSourceCtx.getBuffer();
        AssetBook assetBookChanges;
        this.applyEntityDataToDataSource(entity, _entityCtx.getDatabaseOperation(), assetBookChanges);

        boolean isISOJP = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP], assetBook.DataAreaId);
        boolean isInsertOperation = (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert);

        if (isInsertOperation && assetBook.AssetId && assetBook.BookId)
        {
            if (!assetBook.AssetGroup)
            {
                assetBook.AssetGroup = assetBook.assetTable().AssetGroup;
            }
            assetBook.initFromAssetGroupBookSetup();

            this.applyEntityDataToDataSource(entity, _entityCtx.getDatabaseOperation(), assetBook);
        }

        AssetBook assetBookToCheck;
        if (isInsertOperation)
        {
            assetBookToCheck = assetBook;
        }
        else
        {
            assetBookToCheck = assetBookChanges;
        }

        if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, UsedFromDate)) &&
             assetBookToCheck.isFieldSet(fieldNum(AssetBook, AcquisitionDate)) && !assetBook.IsMigratedAsset)
        {
            assetBook.UsedFromDate = assetBook.AcquisitionDate;
            assetBookChanges.UsedFromDate = assetBook.UsedFromDate;
        }

        if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, DepreciationStartDate)) &&
            (assetBookToCheck.isFieldSet(fieldNum(AssetBook, UsedFromDate)) ||
             assetBookToCheck.isFieldSet(fieldNum(AssetBook, DepreciationConvention))))
        {
            assetBook.calcDepreciationStartDate();
            assetBookChanges.DepreciationStartDate = assetBook.DepreciationStartDate;

            this.applyEntityDataToDataSource(entity, _entityCtx.getDatabaseOperation(), assetBook);
        }

        if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, ServiceLife)) &&
            (assetBookToCheck.isFieldSet(fieldNum(AssetBook, ServiceLifeYears_FR)) ||
             assetBookToCheck.isFieldSet(fieldNum(AssetBook, ServiceLifeMonths_FR))))
        {
            assetBook.calcServiceLife();

            assetBookChanges.ServiceLife = assetBook.ServiceLife;
        }

        if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, LifeTime)) &&
            (assetBookToCheck.isFieldSet(fieldNum(AssetBook, ServiceLife)) ||
             assetBookToCheck.isFieldSet(fieldNum(AssetBook, DepreciationStartDate))))
        {
            assetBook.calcLifeTime();
            assetBook.calcLifeTimeRest();

            assetBookChanges.LifeTime = assetBook.LifeTime;
            assetBookChanges.LifeTimeRest = assetBook.LifeTimeRest;

            this.applyEntityDataToDataSource(entity, _entityCtx.getDatabaseOperation(), assetBook);
        }

        if (isISOJP)
        {
            if (assetBookChanges.isFieldSet(fieldNum(AssetBook, DepreciationExtProfile)))
            {
                if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, SpecialDepAllocationUnit_JP)))
                {
                    AssetSpecialDepHelper_JP::updateDefaultAllocationPeriods(AssetBook, true);
                }
                else if (!assetBookChanges.isFieldSet(fieldNum(AssetBook, SpecialDepAllocationPeriods_JP)))
                {
                    AssetSpecialDepHelper_JP::updateDefaultAllocationPeriods(AssetBook, false);
                }

                if (assetBook.SpecialDepAllocationUnit_JP == AssetAccrualFiscal::Yearly)
                {
                    assetBook.SpecialDepAllocationConvention_JP = AssetSpecialDepAllocationConvention_JP::FollowingFiscalYear;
                }

                assetBook.setSummarizedByMajorType_JP();

                // Skip applying user data as we have this line in the end of the method
            }
        }

        // finally apply user data to avoid user data loss
        this.applyEntityDataToDataSource(entity, _entityCtx.getDatabaseOperation(), assetBook);
    }
}

public boolean validateWrite()
{
    boolean ret;
    AssetGroupBookSetup assetGroupBookSetup;

    ret = super();

    assetGroupBookSetup = AssetGroupBookSetup::find(this.FixedAssetGroupId, this.ValueModelId);

    if (ret && this.IsAssetGroupDepreciation && !AssetParameters::find().DepreciationIncomeTaxAct_IN)
    {
        // Income tax Act depreciation parameter must be selected in the fixed asset parameters to use the Asset group depreciation.
        ret = checkFailed(strFmt("@FixedAssets_AdditionalDepreciation:FA_Message_Validation_CheckAssetGroupBookSetupImportParameter", "@GLS5109", "@GLS5975"));
    }

    if (ret && assetGroupBookSetup && !assetGroupBookSetup.AssetGroupDepreciation_IN && this.IsAssetGroupDepreciation)
    {
        // Asset group depreciation must be equal to Asset group depreciation in the Asset group value models.
        ret = checkFailed(strFmt("@FixedAssets_AdditionalDepreciation:FA_Message_Validation_CheckAssetGroupDepreciation", "@FixedAssets:AssetGroupValueModels"));
    }

    if (ret && assetGroupBookSetup && assetGroupBookSetup.AssetGroupDepreciation_IN && !this.IsAssetGroupDepreciation)
    {
        // Asset group depreciation must be equal to Asset group depreciation in the Asset group value models.
        ret = checkFailed(strFmt("@FixedAssets_AdditionalDepreciation:FA_Message_Validation_CheckAssetGroupDepreciation", "@FixedAssets:AssetGroupValueModels"));
    }

    return ret;
}

private boolean overrideDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    if (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
        || _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
    {
        if (_dataSourceCtx.name() == dataentitydatasourcestr(AssetValueModelEntity, AssetAcquisitionMethod))
        {
            if (this.AcquisitionMethodId == ''
                && this.AcquisitionMethodDescription == '')
            {
                ret = true;
            }
        }

        if (_dataSourceCtx.name() == dataentitydatasourcestr(AssetValueModelEntity, AssetBook))
        {
            AssetBook assetBook = _dataSourceCtx.getBuffer();

            if (assetBook.isFieldSet(fieldNum(AssetBook, IsMigratedAsset)))
            {
                if (assetBook.unreversedDepreciationExists())
                {
                    assetBook.IsMigratedAsset = assetBook.orig().IsMigratedAsset;
                }
            }
        }
    }

    return ret;
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    ret = this.overrideDataSource(_entityCtx, _dataSourceCtx);

    if (!ret)
    {
        ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    ret = this.overrideDataSource(_entityCtx, _dataSourceCtx);

    if (!ret)
    {
        ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}
