/// <summary>
/// Generates a deep link to the CLM Integration Purchase Agreement details form for the given agreement ID.
/// </summary>
/// <param name = "_agreementId">The Agreement ID.</param>
/// <param name = "_companyId">The Legal entity ID.</param>
/// <returns>A link to CLM Integration Purchase Agreement details form for the given agreement ID.</returns>
[SysODataAction('GenerateAgreementFormURLByAgreementId', /*isInstanceMethod:*/ false)]
public static str generateAgreementFormURLByAgreementId(PurchAgreementId _agreementId, DataAreaId _companyId)
{
    str generatedURL = '';

    if (_agreementId && _companyId)
    {
        PurchAgreementHeader agreementHeader = PurchAgreementHeader::findByAgreementId(_agreementId, _companyId);

        if (agreementHeader)
        {
            generatedURL = CLMIntegrationPurchaseAgreementHeaderEntity::generateAgreementFormURL(agreementHeader, _companyId);
        }
    }

    return generatedURL;
}

/// <summary>
/// Generates a deep link to the CLM Integration Purchase Agreement details form for the given external contract ID.
/// </summary>
/// <param name = "_externalContractId">The CLM integration external Contract ID.</param>
/// <param name = "_companyId">The Legal entity ID.</param>
/// <returns>A link to CLM Integration Purchase Agreement details form for the given external contract ID.</returns>
[SysODataAction('GenerateAgreementFormURLByExternalContractId', /*isInstanceMethod:*/ false)]
public static str generateAgreementFormURLByExternalContractId(CLMIntegrationExternalContractId _externalContractId, DataAreaId _companyId)
{
    str generatedURL = '';

    if (_externalContractId && _companyId)
    {
        PurchAgreementHeader agreementHeader = PurchAgreementHeader::findByExternalContractId(_externalContractId, _companyId);

        if (agreementHeader)
        {
            generatedURL = CLMIntegrationPurchaseAgreementHeaderEntity::generateAgreementFormURL(agreementHeader, _companyId);
        }
    }

    return generatedURL;
}

private static str generateAgreementFormURL(PurchAgreementHeader _agreementHeader, DataAreaId _companyId)
{
    var generator = CLMIntegrationUtilities::getUrlGenerator(menuItemDisplayStr(PurchAgreementDetails));
    var requestQueryParameterCollection = generator.RequestQueryParameterCollection;

    generator.Company = _companyId;

    requestQueryParameterCollection.AddRequestQueryParameter(
        formDataSourceStr(PurchAgreement, PurchAgreementHeader),
        fieldStr(PurchAgreementHeader, RecId),
        int642Str(_agreementHeader.RecId));

    return CLMIntegrationUtilities::getAbsoluteUri(generator);
}

public void initValue()
{
    if (!this.skipNumberSequenceCheck())
    {
        NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
            this, fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PurchaseAgreementId), PurchParameters::numRefPurchAgreementId());
    }

    this.BuyingLegalEntityId = this.DataAreaId;

    super();
}

public TableExtension getExtension()
{
    return SysTableExtension::construct();
}

public container getDefaultingDependencies()
{
    return [
        // [field that needs defaulting,
        //      [fields that the default values depends on]]

        [fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, DeliveryModeCode),
                [fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, ShippingCarrierId),
                 fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, ShippingCarrierServiceId)]]];
}

public void defaultField(FieldId _fieldId)
{
    super (_fieldId);

    switch (_fieldId)
    {
        case fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, DeliveryModeCode):
            if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, ShippingCarrierId))
                || this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, ShippingCarrierServiceId)))
            {
                this.DeliveryModeCode = TMSCarrierService::findDlvModeId(this.ShippingCarrierId, this.ShippingCarrierServiceId);
            }
            break;

        case fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PrimaryResponsibleWorkerName),
            fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, SecondaryResponsibleWorkerName):
            if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PurchaseAgreementClassificationName)))
            {
                AgreementClassification agreementClassification = AgreementClassification::findPurchClassification(this.PurchaseAgreementClassificationName);
                this.PrimaryResponsibleWorkerRecId = agreementClassification.PrimaryResponsibleWorker;
                this.SecondaryResponsibleWorkerRecId = agreementClassification.SecondaryResponsibleWorker;
                this.PrimaryResponsibleWorkerName = DirPerson::find(HcmWorker::find(agreementClassification.PrimaryResponsibleWorker).Person).Name;
                this.SecondaryResponsibleWorkerName = DirPerson::find(HcmWorker::find(agreementClassification.SecondaryResponsibleWorker).Person).Name;
            }
            break;
    }
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
            ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, TMSPurchAgreementHeader):
            ret = TMSPurchAgreementHeader::find(PurchAgreementHeader::findAgreementId(this.PurchaseAgreementId).RecId);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    AgreementHeaderDefault agreementHeaderDefault;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader):
            PurchAgreementHeader purchAgreementHeader = _dataSourceCtx.getBuffer();

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert)
            {
                if (!this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementOwnership)))
                {
                    purchAgreementHeader.CLMIntegrationPurchAgreementOwnership = CLMIntegrationPurchaseAgreementOwnershipType::CLM;
                }

                if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber)))
                {
                    purchAgreementHeader.initFromVendTable(VendTable::find(this.AgreementVendorAccountNumber));
                }
            }

            this.setPurchAgreementHeaderIntegration(purchAgreementHeader);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeaderDefault):
            PurchAgreementHeaderDefault purchAgreementHeaderDefault = _dataSourceCtx.getBuffer();

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
                && this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber)))
            {
                purchAgreementHeaderDefault.initFromVendTable(VendTable::find(this.AgreementVendorAccountNumber), this.AgreementVendorAccountNumber);
            }
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementClassification):
            if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PurchaseAgreementClassificationName)))
            {
                AgreementClassification agreementClassification = _dataSourceCtx.getBuffer();
                agreementClassification.AgreementRelationType = tablenum(PurchAgreementHeader);
            }
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
            if (_dataSourceCtx.isAnyEntityFieldSet(this))
            {
                this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
            }
            else
            {
                agreementHeaderDefault = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementHeaderDefault)).getBuffer();
                if (agreementHeaderDefault.DeliveryPostalAddress)
                {
                    _dataSourceCtx.setBuffer(this.findLogisticsPostalAddressBaseEntityByRecId(agreementHeaderDefault.DeliveryPostalAddress));
                    _dataSourceCtx.setFieldStateForAllFields(FieldState::Set);
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                    this.mapDataSourceToEntity(_entityCtx, _dataSourceCtx);
                }
            }
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeaderExt_RU):
            #ISOCountryRegionCodes

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                PurchAgreementHeaderExt_RU purchAgreementHeaderExt = _dataSourceCtx.getBuffer();

                if (!purchAgreementHeaderExt.AgreementId)
                {
                    PurchAgreementHeader purchAgreementHeaderLoc = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader)).getBuffer();
                    purchAgreementHeaderExt.initFromAgreementHeader(purchAgreementHeaderLoc);
                }
            }
            else 
            {
                _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            }
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, CLMIntegrationContractDocumentRelationship):
            if (this.ContractId && this.ContractLegalEntityId)
            {
                CLMIntegrationContractDocumentRelationship contractDocumentRelationship = _dataSourceCtx.getBuffer();
                contractDocumentRelationship.DocumentDataAreaId = appl.company().dataArea(tableNum(PurchAgreementHeader));
            }
            else
            {
                _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            }
            break;
    }

    super(_entityCtx, _dataSourceCtx);
}

public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementHeaderDefault):
            AgreementHeaderDefault agreementHeaderDefault = _dataSourceCtx.getBuffer();
            this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(_entityCtx, agreementHeaderDefault.DeliveryPostalAddress);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader):
            PurchAgreementHeader purchAgreementHeader = _dataSourceCtx.getBuffer();
            if (this.DefaultLedgerDimension != purchAgreementHeader.DefaultDimension)
            {
                this.DefaultLedgerDimensionDisplayValue = DimensionSetEntity::find(purchAgreementHeader.DefaultDimension).DisplayValue;
                this.DefaultLedgerDimension = purchAgreementHeader.DefaultDimension;
            }

            if (this.PreparerRecId != purchAgreementHeader.Originator)
            {
                this.PreparerRecId = purchAgreementHeader.Originator;
                this.PreparerPersonPartyNumber = DirPerson::find(purchAgreementHeader.Originator).PartyNumber;
            }
            break;
    }

    super(_entityCtx, _dataSourceCtx);
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    this.defaultRowOnDataSource(_dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader):
            this.validateWriteAgreementHeader(_dataSourceCtx);
            this.setResponsibleWorker(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementHeaderDefault):
            AgreementHeaderDefault agreementHeaderDefault = _dataSourceCtx.getBuffer();
            TMSPurchAgreementHeader tmsPurchAgreementHeader = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, TMSPurchAgreementHeader)).getBuffer();

            if (agreementHeaderDefault.isFieldSet(fieldNum(AgreementHeaderDefault, DeliveryMode)))
            {
                tmsPurchAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
            }

            this.setAgreementHeaderDefaultIntegration(agreementHeaderDefault);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeaderDefault):
            PurchAgreementHeaderDefault purchAgreementHeaderDefault = _dataSourceCtx.getBuffer();
            this.setPurchAgreementHeaderDefaultIntegration(purchAgreementHeaderDefault);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, TMSPurchAgreementHeader):
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    if (ret)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
                this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                break;
        }
    }

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    this.defaultRowOnDataSource(_dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader):
            this.validateWriteAgreementHeader(_dataSourceCtx);
            this.setResponsibleWorker(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementHeaderDefault):
            this.validateWriteAgreementHeaderDefault(_entityCtx, _dataSourceCtx);
            AgreementHeaderDefault agreementHeaderDefault = _dataSourceCtx.getBuffer();
            TMSPurchAgreementHeader tmsPurchAgreementHeader = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, TMSPurchAgreementHeader)).getBuffer();

            if (agreementHeaderDefault.isFieldSet(fieldNum(AgreementHeaderDefault, DeliveryMode)))
            {
                tmsPurchAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
            }

            this.setAgreementHeaderDefaultIntegration(agreementHeaderDefault);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeaderDefault):
            PurchAgreementHeaderDefault purchAgreementHeaderDefault = _dataSourceCtx.getBuffer();
            this.setPurchAgreementHeaderDefaultIntegration(purchAgreementHeaderDefault);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

public boolean deleteEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            break;

        case dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementHeaderDefault):
            this.validateWriteAgreementHeaderDefault(_entityCtx, _dataSourceCtx);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

private void setPurchAgreementHeaderIntegration(PurchAgreementHeader _purchAgreementHeader)
