    if (CLMIntegrationFeature::isEnabled())
    {
        _purchAgreementHeader.IsIntegration = PurchAgreementIntegrationType::CLM;
    }
}

private void setPurchAgreementHeaderDefaultIntegration(PurchAgreementHeaderDefault _purchAgreementHeaderDefault)
{
    if (CLMIntegrationFeature::isEnabled())
    {
        _purchAgreementHeaderDefault.IsIntegration = PurchAgreementIntegrationType::CLM;
    }
}

private void setAgreementHeaderDefaultIntegration(AgreementHeaderDefault _agreementHeaderDefault)
{
    if (CLMIntegrationFeature::isEnabled())
    {
        _agreementHeaderDefault.IsIntegration = PurchAgreementIntegrationType::CLM;
    }
}

