// Call this method when defaulting must happen prior to data source validation
private void defaultRowOnDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (!_dataSourceCtx.skipDefaultRow())
    {
        _dataSourceCtx.getBuffer().defaultRow();
        _dataSourceCtx.skipDefaultRow(true);
    }
}

private static str isDeliveryAddressAgreementSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressAgreementSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scnearios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

private LogisticsPostalAddressBaseEntity findLogisticsPostalAddressBaseEntityByRecId(RecId _recId)
{
    LogisticsPostalAddressBaseEntity logisticsPostalAddressBaseEntity;

    select firstonly logisticsPostalAddressBaseEntity
        where logisticsPostalAddressBaseEntity.AddressRecId == _recId;

    return logisticsPostalAddressBaseEntity;
}

private void validateWriteAgreementHeader(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    PurchAgreementHeader purchAgreementHeader = _dataSourceCtx.getBuffer();

    var dataProvider = PurchAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(purchAgreementHeader);

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber)) && !dataProvider.canEditVendor())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber));
    }

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementStatus)) && !dataProvider.canEditAgreementState())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, AgreementStatus));
    }

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultCommitmentType)) && !dataProvider.canEditDefaultAgreementLineType())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultCommitmentType));
    }

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultEffectiveDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultEffectiveDate));
    }

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultExpirationDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, DefaultExpirationDate));
    }

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, CurrencyCode)) && !dataProvider.canEditCurrency())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, CurrencyCode));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }

    this.setPurchAgreementHeaderIntegration(purchAgreementHeader);
}

private void validateWriteAgreementHeaderDefault(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    PurchAgreementHeader purchAgreementHeader = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(CLMIntegrationPurchaseAgreementHeaderEntity, PurchAgreementHeader)).getBuffer();
    var dataProvider = PurchAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(purchAgreementHeader);

    if (this.isFieldModified(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, ProjectId)) && !dataProvider.canEditProjectId())
    {
        isValid = this.allowEditCheckFailed(fieldPName(CLMIntegrationPurchaseAgreementHeaderEntity, ProjectId));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

private boolean allowEditCheckFailed(FieldLabel _fieldLabel)
{
    return checkFailed(strFmt("@ApplicationFoundation:DataEntitiesNoAllowEdit", _fieldLabel));
}

private boolean isFieldModified(FieldId _fieldId)
{
    return DataEntityDataSourceRuntimeContextAppSuite_Extension::isFieldModified(this, _fieldId);
}

public static Query defaultCTQuery()
{
    Query purchAgreementHeaderQuery = new Query();

    var purchAgreementHeaderDS = purchAgreementHeaderQuery.addDataSource(tableNum(PurchAgreementHeader));

    var agreementClassificationDS = purchAgreementHeaderDS.addDataSource(tableNum(AgreementClassification));
    agreementClassificationDS.joinMode(JoinMode::InnerJoin);
    agreementClassificationDS.relations(true);

    var dirPersonDS = purchAgreementHeaderDS.addDataSource(tableNum(DirPerson));
    dirPersonDS.joinMode(JoinMode::OuterJoin);
    dirPersonDS.relations(true);

    var companyInfoDS = purchAgreementHeaderDS.addDataSource(tableNum(CompanyInfo));
    companyInfoDS.joinMode(JoinMode::OuterJoin);
    companyInfoDS.addLink(fieldNum(PurchAgreementHeader, BuyingLegalEntity), fieldNum(CompanyInfo, RecId));

    var dimensionSetEntityDS = purchAgreementHeaderDS.addDataSource(tableNum(DimensionAttributeValueSet));
    dimensionSetEntityDS.joinMode(JoinMode::OuterJoin);
    dimensionSetEntityDS.addLink(fieldNum(PurchAgreementHeader, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));

    var purchAgreementHeaderDefaultDS = purchAgreementHeaderDS.addDataSource(tableNum(PurchAgreementHeaderDefault));
    purchAgreementHeaderDefaultDS.joinMode(JoinMode::OuterJoin);
    purchAgreementHeaderDefaultDS.addLink(fieldNum(PurchAgreementHeader, RecId), fieldNum(PurchAgreementHeaderDefault, PurchaseAgreementHeader));

    var agreementHeaderDefaultDS = purchAgreementHeaderDS.addDataSource(tableNum(AgreementHeaderDefault));
    agreementHeaderDefaultDS.joinMode(JoinMode::OuterJoin);
    agreementHeaderDefaultDS.addLink(fieldNum(PurchAgreementHeader, RecId), fieldNum(AgreementHeaderDefault, AgreementHeader));

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(agreementHeaderDefaultDS, fieldNum(AgreementHeaderDefault, DeliveryPostalAddress));

    var tmsPurchAgreementHeaderDS = purchAgreementHeaderDS.addDataSource(tableNum(TMSPurchAgreementHeader));
    tmsPurchAgreementHeaderDS.joinMode(JoinMode::OuterJoin);
    tmsPurchAgreementHeaderDS.addLink(fieldNum(PurchAgreementHeader, RecId), fieldNum(TMSPurchAgreementHeader, RefRecId));

    return purchAgreementHeaderQuery;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PrimaryResponsibleWorkerName):
            case fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, SecondaryResponsibleWorkerName):
                if (this.PrimaryResponsibleWorkerName != ''
                    && this.PrimaryResponsibleWorkerName == this.SecondaryResponsibleWorkerName)
                {
                    ret = checkFailed(strFmt("@SCM:ErrorSecondaryResponsibleWorkerSameAsThePrimary"));
                }
                else if (this.PrimaryResponsibleWorkerName == ''
                    && this.SecondaryResponsibleWorkerName != '')
                {
                    ret = checkFailed(strFmt("@SCM:ErrorSecondaryResponsibleWorkerWithoutAPrimary"));
                }
                break;
        }
    }

    return ret;
}

private void setResponsibleWorker(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    PurchAgreementHeader purchAgreementHeader = _dataSourceCtx.getBuffer();

    if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PrimaryResponsibleWorkerName)) ||
        this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, PrimaryResponsibleWorkerEmail)))
    {
        purchAgreementHeader.PrimaryResponsibleWorker = this.getResponsibleWorker(this.PrimaryResponsibleWorkerName, this.PrimaryResponsibleWorkerEmail);
    }

    if (this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, SecondaryResponsibleWorkerName)) ||
        this.isFieldSet(fieldNum(CLMIntegrationPurchaseAgreementHeaderEntity, SecondaryResponsibleWorkerEmail)))
    {
        purchAgreementHeader.SecondaryResponsibleWorker = this.getResponsibleWorker(this.SecondaryResponsibleWorkerName, this.SecondaryResponsibleWorkerEmail);
    }
}

private HcmWorkerRecId getResponsibleWorker(Name _name, Email _email)
{
    HcmWorker hcmWorker;

    if (_name)
    {
        DirPerson dirPerson;

        select firstonly RecId from hcmWorker
            exists join dirPerson
                where dirPerson.RecId == hcmWorker.Person
                    && dirPerson.Name == _name;
    }

    if (!hcmWorker.RecId && _email)
    {
        DirPersonUser dirPersonUser;
        UserInfo userInfo;

        select firstonly RecId from hcmWorker
            exists join dirPersonUser
                where dirPersonUser.PersonParty == hcmWorker.Person
                exists join userInfo
                    where userInfo.id == dirPersonUser.User
                        && userInfo.NetworkAlias == _email;
    }

    return hcmWorker.RecId;
}

