Method.Source:
/// <summary>
/// Returns the name of a product dimension datasource.
/// </summary>
/// <param name = "_inventProductDimension">A product dimension field.</param>
/// <returns>The name of the product dimension's datasource.</returns>
[Hookable, Wrappable]
protected static str productDimensionDataSourceName(InventProductDimension _inventProductDimension)
{
    switch (true)
    {
        case _inventProductDimension is InventProductDimensionConfig:
            return dataEntityDataSourceStr(EcoResProductVariantV2Entity, EcoResProductVariantConfiguration);
        case _inventProductDimension is InventProductDimensionColor:
            return dataEntityDataSourceStr(EcoResProductVariantV2Entity, EcoResProductVariantColor);
        case _inventProductDimension is InventProductDimensionStyle:
            return dataEntityDataSourceStr(EcoResProductVariantV2Entity, EcoResProductVariantStyle);
        case _inventProductDimension is InventProductDimensionVersion:
            return dataEntityDataSourceStr(EcoResProductVariantV2Entity, EcoResProductVariantVersion);
        case _inventProductDimension is InventProductDimensionSize:
            return dataEntityDataSourceStr(EcoResProductVariantV2Entity, EcoResProductVariantSize);
    }

    return '';
}

/// <summary>
/// Returns the field Id for a product dimension.
/// </summary>
/// <param name = "_inventProductDimension">A product dimension field.</param>
/// <returns>Field Id on <c>EcoResProductVariantV2Entity</c> table.</returns>
[Hookable, Wrappable]
protected static FieldId productDimensionFieldId(InventProductDimension _inventProductDimension)
{
    switch (true)
    {
        case _inventProductDimension is InventProductDimensionConfig:
            return fieldNum(EcoResProductVariantV2Entity, ProductConfigurationId);
        case _inventProductDimension is InventProductDimensionColor:
            return fieldNum(EcoResProductVariantV2Entity, ProductColorId);
        case _inventProductDimension is InventProductDimensionStyle:
            return fieldNum(EcoResProductVariantV2Entity, ProductStyleId);
        case _inventProductDimension is InventProductDimensionVersion:
            return fieldNum(EcoResProductVariantV2Entity, ProductVersionId);
        case _inventProductDimension is InventProductDimensionSize:
            return fieldNum(EcoResProductVariantV2Entity, ProductSizeId);
    }

    return 0;
}

