private void setEntityFields()
{
    FiscalEstablishment_BR fiscalEstablishment = FiscalEstablishment_BR::findById(this.FiscalEstablishmentId);

    this.FiscalEstablishmentCNPJorCPF = fiscalEstablishment.CNPJCPFNum_BR;
    this.FiscalEstablishmentIE = fiscalEstablishment.IENum_BR;
    this.FiscalEstablishmentCCM = fiscalEstablishment.CCMNum_BR;
    this.FiscalEstablishmentName = fiscalEstablishment.Name;
    this.FiscalEstablishmentTelephone = fiscalEstablishment.phone();
    this.FiscalEstablishmentAddress = fiscalEstablishment.postalAddress().RecId;

    if (this.CarrierName)
    {
        SalesCarrier salesCarrier = SalesCarrier::find(this.CarrierName);

        this.Carrier = salesCarrier.RecId;
        this.CarrierCNPJorCPF = salesCarrier.CNPJCPFNum_BR;
        this.CarrierIE = salesCarrier.IENum_BR;
        this.CarrierName = salesCarrier.CarrierName;
        this.CarrierAddress = LogisticsPostalAddress::findByLocation(salesCarrier.Location).RecId;
    }
}

private DirPartyRecId getThirdPartyPartyId()
{
    DirPartyRecId party;

    switch (this.ThirdPartyAccountType)
    {
        case CustVendType_BR::Customer:
            party = CustTable::find(this.ThirdPartyAccount).Party;
            break;

        case CustVendType_BR::Vendor:
            party = VendTable::find(this.ThirdPartyAccount).Party;
            break;

        case CustVendType_BR::FiscalEstablishment:
            throw error(error::wrongUseOfFunction(funcName()));
    }

    return party;
}

private void findAddressDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx, LogisticsAddressCountryRegionId addressCountryRegionId, LogisticsAddressStateId addressState, LogisticsAddressZipCodeId addressZipCode,
    LogisticsAddressCityName addressCity, LogisticsAddressStreet addressStreet, LogisticsAddressStreetNumber addressStreetNumber, LogisticsAddressBuildingCompliment addressBuildingCompliment, LogisticsAddressDistrictName addressDistrictName)
{
    LogisticsPostalAddressBaseEntity logisticsPostalAddress;

    if (this.ThirdPartyAccountType == CustVendType_BR::FiscalEstablishment)
    {
        RecId logisticsPostalAddressRecId = FiscalEstablishment_BR::findById(this.ThirdPartyAccount).postalAddress().RecId;
        select logisticsPostalAddress where logisticsPostalAddress.AddressRecId == logisticsPostalAddressRecId;
    }
    else
    {
        DirPartyLocation dirPartyLocation;
        DirPartyRecId party = this.getThirdPartyPartyId();

        select logisticsPostalAddress
        where logisticsPostalAddress.CountryRegionId     == addressCountryRegionId
            && logisticsPostalAddress.State              == addressState
            && logisticsPostalAddress.ZipCode            == addressZipCode
            && logisticsPostalAddress.City               == addressCity
            && logisticsPostalAddress.Street             == addressStreet
            && logisticsPostalAddress.StreetNumber       == addressStreetNumber
            && logisticsPostalAddress.BuildingCompliment == addressBuildingCompliment
            && logisticsPostalAddress.DistrictName       == addressDistrictName
        exists join dirPartyLocation
        where dirPartyLocation.Location == logisticsPostalAddress.LocationRecId
           && dirPartyLocation.Party == party;
    }

    if (logisticsPostalAddress.RecId)
    {
        _dataSourceCtx.setBuffer(logisticsPostalAddress);
        _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
    }
}

private RecId saveAddress(DataEntityRuntimeContext _entityCtx, str _dataSourceName, LogisticsLocationRoleType _addressType)
{
    DataEntityDataSourceRuntimeContext addressDataSourceContext = _entityCtx.getRuntimeContextByName(_dataSourceName);

    if (addressDataSourceContext.getDatabaseOperation() != DataEntityDatabaseOperation::None)
    {
        LogisticsLocationRecId locationRecId = LogisticsPostalAddressBaseEntity::saveAddress(_entityCtx, _dataSourceName);
        DirPartyRecId party = this.getThirdPartyPartyId();

        DirPartyLocationContract partyLocationContract = DirPartyLocationContract::constructForPartyLocation(party, locationRecId);
        partyLocationContract.parmIsPostalAddress(true);
        partyLocationContract.parmIsPrimary((_addressType == LogisticsLocationRoleType::Business));
        partyLocationContract.parmIsPrivate(false);
        partyLocationContract.parmRoleIds([LogisticsLocationRole::findBytype(_addressType).RecId]);

        DirParty::createOrUpdateLocation(partyLocationContract);
    }

    LogisticsPostalAddressBaseEntity address = addressDataSourceContext.getBuffer();
    return address.AddressRecId;
}

private void setFiscalDocumentFields(DataEntityRuntimeContext entityCtx, FiscalDocument_BR fiscalDocument)
{
    FiscalDocDateWithTimeZoneHelper_BR fiscalDocDateWithTimeZoneHelper = FiscalDocDateWithTimeZoneHelper_BR::newFromFiscalDocument(fiscalDocument);
    fiscalDocument.FiscalDocumentDateTime       = fiscalDocDateWithTimeZoneHelper.getFiscalDocumentDateTime();
    fiscalDocument.FiscalDocumentDateTimeOffset = fiscalDocDateWithTimeZoneHelper.getFiscalDocumentDateTimeOffset();

    fiscalDocument.Version = FiscalDocumentVersion_BR::v1;

    fiscalDocument.ThirdPartyPostalAddress = this.saveAddress(entityCtx, dataEntityDataSourceStr(FiscalDocumentHeaderEntity, ThirdPartyPostalAddress), LogisticsLocationRoleType::Business);

    if (this.isDeliveryAddressSet())
    {
        fiscalDocument.DeliveryLogisticsPostalAddress = this.saveAddress(entityCtx, dataEntityDataSourceStr(FiscalDocumentHeaderEntity, DeliveryPostalAddress), LogisticsLocationRoleType::Delivery);
    }
}

private boolean isDeliveryAddressSet()
{
    return this.DeliveryAddressCountryRegionId || this.DeliveryAddressState || this.DeliveryAddressZipCode || this.DeliveryAddressCity
        || this.DeliveryAddressStreet || this.DeliveryAddressStreetNumber || this.DeliveryAddressBuildingCompliment || this.DeliveryAddressDistrictName;
}

 private void executeValidation()
 {
     FiscalDocumentHeaderEntity fiscalDocumentHeaderEntity;
     FiscalDocumentHeaderStaging fiscalDocumentHeaderStaging;

     select count(RecId) from fiscalDocumentHeaderStaging
         join fiscalDocumentHeaderEntity
         where fiscalDocumentHeaderStaging.TransferStatus != DMFTransferStatus::Completed
            && fiscalDocumentHeaderStaging.FiscalEstablishmentId == fiscalDocumentHeaderEntity.FiscalEstablishmentId
            && fiscalDocumentHeaderStaging.Direction == fiscalDocumentHeaderEntity.Direction
            && fiscalDocumentHeaderStaging.Status == fiscalDocumentHeaderEntity.Status
            && fiscalDocumentHeaderStaging.Series == fiscalDocumentHeaderEntity.Series
            && fiscalDocumentHeaderStaging.FiscalDocumentNumber == fiscalDocumentHeaderEntity.FiscalDocumentNumber
            && fiscalDocumentHeaderStaging.ThirdPartyCNPJorCPF == fiscalDocumentHeaderEntity.ThirdPartyCNPJorCPF
            && fiscalDocumentHeaderEntity.RecId == this.RecId;

     if (fiscalDocumentHeaderStaging.RecId > 1)
     {
         throw error('@Brazil:FiscalDocumentHeaderEntityDuplicatedRecord');
     }

     FiscalDocParmDataCreatorFiscalDocEntity_BR parmDataCreator = new FiscalDocParmDataCreatorFiscalDocEntity_BR(this);
     FiscalDocumentParmData_BR fiscalDocumentParmData = parmDataCreator.createParmData();
     FiscalDocumentPost_BR fiscalDocumentPost = new FiscalDocumentPost_BR(fiscalDocumentParmData);

     if (!fiscalDocumentPost.canBePosted())
     {
         throw Exception::Error;
     }
}

public void persistEntity(DataEntityRuntimeContext _entityCtx)
{
    this.setEntityFields();

    super(_entityCtx);

    this.executeValidation();
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(FiscalDocumentHeaderEntity, FiscalDocument_BR))
    {
        this.setFiscalDocumentFields(_entityCtx, _dataSourceCtx.getBuffer());
    }

    ret = super(_entityCtx, _dataSourceCtx);

    return ret;
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(FiscalDocumentHeaderEntity, ThirdPartyPostalAddress):
            this.findAddressDataSource(_dataSourceCtx,
                this.ThirdPartyAddressCountryRegionId,
                this.ThirdPartyAddressState,
                this.ThirdPartyAddressZipCode,
                this.ThirdPartyAddressCity,
                this.ThirdPartyAddressStreet,
                this.ThirdPartyAddressStreetNumber,
                this.ThirdPartyAddressBuildingCompliment,
                this.ThirdPartyAddressDistrictName);

            break;

        case dataEntityDataSourceStr(FiscalDocumentHeaderEntity, DeliveryPostalAddress):
            if (this.isDeliveryAddressSet())
            {
                this.findAddressDataSource(_dataSourceCtx,
                    this.DeliveryAddressCountryRegionId,
                    this.DeliveryAddressState,
                    this.DeliveryAddressZipCode,
                    this.DeliveryAddressCity,
                    this.DeliveryAddressStreet,
                    this.DeliveryAddressStreetNumber,
                    this.DeliveryAddressBuildingCompliment,
                    this.DeliveryAddressDistrictName);
            }
            else
            {
                _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            }

            break;
    }
}
