Method.Source:
/// <summary>
//  Resolves the record for the backing data source.
/// </summary>
/// <param name = "_entityCtx"> The context for the data entity.</param>
/// <param name = "_dataSourceCtx">The context for the data source.</param>
/// <returns>The resolved record</returns>
public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, BudgetPlanningProcess))
    {
        BudgetPlanningProcess dataSourceBuffer = _dataSourceCtx.getBuffer();

        BudgetPlanningProcess planningProcess = null;
        planningProcess.selectForUpdate(False);

        SELECT FIRSTONLY
            planningProcess
            WHERE (planningProcess.Name == dataSourceBuffer.Name
            && planningProcess.Ledger == Ledger::current());

        return planningProcess;
    }

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastCompensationGrid))
    {
        HcmPositionForecastScenario positionForecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario)).getBuffer() as HcmPositionForecastScenario;
        HcmForecastScenario forecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmForecastScenario)).getBuffer() as HcmForecastScenario;

        positionForecastScenarioDS.ForecastScenario = forecastScenarioDS.RecId;

        positionForecastScenarioDS.CompensationGroupDataAreaId = CompanyInfo::find(positionForecastScenarioDS.LegalEntity).DataArea;

        EffectiveDate positionForecastScenarioActivationDate = positionForecastScenarioDS.getPositionForecastDateRangeStartDate();
        ExpirationDate positionForecastScenarioRetirementDate = positionForecastScenarioDS.getPositionForecastDateRangeEndDate();
        HRCCompRefPointSetup refPointSetupId;

        HcmPositionForecastCompensationGrid compGridDs = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastCompensationGrid)).getBuffer() as HcmPositionForecastCompensationGrid;
        HcmPositionForecastCompensationGrid compGridPositionForecastScenario = HcmPositionForecastCompensationGrid::getCompensationGridByRefPointIdCompGroupWithInDateRange(compGridDs.RefPointId,
                                                                                                                        compGridDs.RefPointSetupId,
                                                                                                                        positionForecastScenarioActivationDate,
                                                                                                                        positionForecastScenarioRetirementDate,
                                                                                                                        positionForecastScenarioDs.CompensationGroupId,
                                                                                                                        positionForecastScenarioDs.CompensationGroupDataAreaId,
                                                                                                                        compGridDs.CompensationLevel);

        return compGridPositionForecastScenario;
    }

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastCompensationGrid_CostElement))
    {
        HcmPositionForecastScenario positionForecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario)).getBuffer() as HcmPositionForecastScenario;
        HcmForecastScenario forecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmForecastScenario)).getBuffer() as HcmForecastScenario;

        if (!positionForecastScenarioDS.CompensationGroupId || !positionForecastScenarioDS.PositionForecastCompensationGrid)
        {
            error("@SYP4940008");
            return super(_entityCtx, _dataSourceCtx);
        }
        positionForecastScenarioDS.ForecastScenario = forecastScenarioDS.RecId;
        positionForecastScenarioDS.CompensationGroupDataAreaId = CompanyInfo::find(positionForecastScenarioDS.LegalEntity).DataArea;

        EffectiveDate positionForecastScenarioActivationDate = positionForecastScenarioDS.getPositionForecastDateRangeStartDate();
        ExpirationDate positionForecastScenarioRetirementDate = positionForecastScenarioDS.getPositionForecastDateRangeEndDate();
        HRCCompRefPointSetup refPointSetupId;

        HcmPositionForecastCompensationGrid compGridLineDs = _dataSourceCtx.getBuffer() as HcmPositionForecastCompensationGrid;
        HcmPositionForecastCompensationGrid compGridLine = HcmPositionForecastCompensationGrid::getCompensationGridByRefPointIdCompGroupWithInDateRange(compGridLineDs.RefPointId,
                                                                                                    compGridLineDs.RefPointSetupId,
                                                                                                    positionForecastScenarioActivationDate,
                                                                                                    positionForecastScenarioRetirementDate,
                                                                                                    positionForecastScenarioDs.CompensationGroupId,
                                                                                                    positionForecastScenarioDs.CompensationGroupDataAreaId, 
                                                                                                    compGridLineDs.CompensationLevel);

        return compGridLine;
    }

    ret = super(_entityCtx, _dataSourceCtx);

    return ret;
}

/// <summary>
/// Inserts the backing entity to the data source.
/// </summary>
/// <param name = "_entityCtx"> The context for the data entity.</param>
/// <param name = "_dataSourceCtx">The context for the data source.</param>
/// <returns>true, if the records gets inserted; otherwise, false</returns>
public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario))
    {
        HcmPositionForecastScenario positionForecastScenarioDS = _dataSourceCtx.getBuffer() as HcmPositionForecastScenario;

        HcmForecastScenario forecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmForecastScenario)).getBuffer() as HcmForecastScenario;
        positionForecastScenarioDS.LegalEntity = forecastScenarioDS.LegalEntity;
    }

    ret = super(_entityCtx, _dataSourceCtx);
    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastBudgetAcctLine))
    {
        HcmPositionForecastBudgetAcctLine positionForecastBudgetAcctLine = _dataSourceCtx.getBuffer() as HcmPositionForecastBudgetAcctLine;
        HcmPositionForecastScenario positionForecastScenario = HcmPositionForecastScenario::find(positionForecastBudgetAcctLine.PositionForecastScenario);

        HcmPositionForecastCalcProcessLauncher positionForecastLauncher = HcmPositionForecastCalcProcessLauncher::newForecastCalculateSinglePosition(positionForecastScenario);
        positionForecastLauncher.runPositionForecstCalculationProcess();
    }

    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario))
    {

        HcmPositionForecastDetail hcmPositionForecastDetailDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastDetail)).getBuffer() as HcmPositionForecastDetail;
        HcmPositionForecastScenario positionForecastScenarioDS = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario)).getBuffer() as HcmPositionForecastScenario;
        HcmPositionForecastDetailV2 detailV2 = HcmPositionForecastDetailV2::findByPositionForecastScenerio(positionForecastScenarioDS.RecId, hcmPositionForecastDetailDS.PositionForecast);

        if (!detailV2)
        {
            this.createPositionForecastDetailV2(hcmPositionForecastDetailDS, positionForecastScenarioDS);
        }
    }

    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcMPositionForecast))
    {
        if (HcMPositionForecastEntityHierarchyRecordInsertFlight::instance().isEnabled())
        {
            HcMPositionForecast hcMPositionForecast = _dataSourceCtx.getBuffer() as HcMPositionForecast;

            if (hcMPositionForecast)
            {
                this.createPositionForecastHierarchy(hcMPositionForecast);
            }
        }
    }

    return ret;
}

/// <summary>
/// Creates a record in the <c>HcmPositionForecastDetail</c> table from the passed in parameters.
/// </summary>
/// <returns>
/// true if the record creation was successful; otherwise, false.
/// </returns>
/// <remarks>
/// A redundant check on maximum number of positions for a given job is coded to check for concurrent
/// processes trying to create positions.
/// </remarks>
private void createPositionForecastDetailV2(HcmPositionForecastDetail _hcmPositionForecastDetail,HcmPositionForecastScenario _positionForecastScenario)
{
    HcmPositionForecastDetailV2  hcmPositionForecastDetailV2;

    hcmPositionForecastDetailV2.PositionForecast = _hcmPositionForecastDetail.PositionForecast;
    hcmPositionForecastDetailV2.CompLocation = _hcmPositionForecastDetail.CompLocation;
    hcmPositionForecastDetailV2.Department = _hcmPositionForecastDetail.Department;
    hcmPositionForecastDetailV2.Description = _hcmPositionForecastDetail.Description;
    hcmPositionForecastDetailV2.PositionType = _hcmPositionForecastDetail.PositionType;
    hcmPositionForecastDetailV2.Title = _hcmPositionForecastDetail.Title;
    hcmPositionForecastDetailV2.Job = _hcmPositionForecastDetail.Job;
    hcmPositionForecastDetailV2.PositionForecastScenario = _positionForecastScenario.RecId;


    // If this validateWrite fails for one, it should fail for all the rest of positions
    if (hcmPositionForecastDetailV2.validateWrite())
    {
        ttsbegin;
        hcmPositionForecastDetailV2.write();    
        ttscommit;
    }
}

private void createPositionForecastHierarchy(HcMPositionForecast _hcMPositionForecast)
{
    HcmPositionForecastHierarchy hcmPositionForecastHierarchy;
    HcmPositionHierarchy positionHierarchy = HcmPositionHierarchy::findByPositionAndHierarchyType(_hcMPositionForecast.Position, HcmPositionHierarchyType::findLineType().RecId);

    hcmPositionForecastHierarchy.clear();
    hcmPositionForecastHierarchy.PositionForecast = _hcMPositionForecast.RecId;
    hcmPositionForecastHierarchy.PositionHierarchyType = HcmPositionHierarchyType::lineHierarchyType();

    if (positionHierarchy.RecId)
    {
        hcmPositionForecastHierarchy.ParentPosition = positionHierarchy.ParentPosition;
    }

    if (hcmPositionForecastHierarchy.validateWrite())
    {
        ttsbegin;
        hcmPositionForecastHierarchy.write();
        ttscommit;
    }
}

private boolean updatePositionForecastDetailV2(HcmPositionForecastDetail _positionForecastDetail, HcmPositionForecastDetailV2 _positionForecastDetailV2)
{
    boolean attemptUpdate = false;

    if (_positionForecastDetailV2.Description != _positionForecastDetail.Description)
    {
        _positionForecastDetailV2.Description = _positionForecastDetail.Description;
        attemptUpdate = true;
    }

    if (_positionForecastDetailV2.Title != _positionForecastDetail.title)
    {
        _positionForecastDetailV2.Title = _positionForecastDetail.title;
        attemptUpdate = true;
    }

    if (_positionForecastDetailV2.Department != _positionForecastDetail.Department)
    {
        _positionForecastDetailV2.Department = _positionForecastDetail.Department;
        attemptUpdate = true;
    }

    if (_positionForecastDetailV2.CompLocation != _positionForecastDetail.CompLocation)
    {
        _positionForecastDetailV2.CompLocation = _positionForecastDetail.CompLocation;
        attemptUpdate = true;
    }

    if (_positionForecastDetailV2.Job != _positionForecastDetail.Job)
    {
        _positionForecastDetailV2.Job = _positionForecastDetail.Job;
        attemptUpdate = true;
    }

    if (_positionForecastDetailV2.PositionType != _positionForecastDetail.PositionType)
    {
        _positionForecastDetailV2.PositionType = _positionForecastDetail.PositionType;
        attemptUpdate = true;
    }

    if (attemptUpdate)
    {
        if (_positionForecastDetailV2.validateWrite())
        {
            ttsbegin;
            _positionForecastDetailV2.write();
            ttscommit;
        }
        else
        {
            attemptUpdate = false;
        }
    }

    return attemptUpdate;
}

/// <summary>
/// Updates the backing entity to the data source.
/// </summary>
/// <param name = "_entityCtx">The context for the data entity.</param>
/// <param name = "_dataSourceCtx">The context for the data source.</param>
/// <returns>true, if the records gets inserted; otherwise, false</returns>
public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;

    ret = super(_entityCtx, _dataSourceCtx);
    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastBudgetAcctLine))
    {
        HcmPositionForecastBudgetAcctLine positionForecastBudgetAcctLine = _dataSourceCtx.getBuffer() as HcmPositionForecastBudgetAcctLine;
        HcmPositionForecastScenario positionForecastScenario = HcmPositionForecastScenario::find(positionForecastBudgetAcctLine.PositionForecastScenario);

        HcmPositionForecastCalcProcessLauncher positionForecastLauncher = HcmPositionForecastCalcProcessLauncher::newForecastCalculateSinglePosition(positionForecastScenario);
        positionForecastLauncher.runPositionForecstCalculationProcess();
        positionForecastBudgetAcctLine.reread();
    }

    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastDetail))
    {
        HcmPositionForecastDetail hcmPositionForecastDetailDS = _dataSourceCtx.getBuffer() as HcmPositionForecastDetail;
        HcmForecastScenario forecastScenario = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(HcmPositionForecastEntity, HcmForecastScenario)).getBuffer() as HcmForecastScenario;
        HcmPositionForecastScenario positionForecastScenario = HcmPositionForecastScenario::findByPositionForecastBudgetPlanScenario(hcmPositionForecastDetailDS.PositionForecast, forecastScenario.RecId);

        HcmPositionForecastDetailV2 detailV2 = HcmPositionForecastDetailV2::findByPositionForecastScenerio(positionForecastScenario.RecId, hcmPositionForecastDetailDS.PositionForecast, true);

        if (detailV2)
        {
            this.updatePositionForecastDetailV2(hcmPositionForecastDetailDS, detailV2);
        }
    }


    return ret;
}

/// <summary>
/// Maps the backing entity to the datasource.
/// Checks for certain changes and executes the necessary defaulting logic.
/// </summary>
/// <param name = "_entityCtx">The context for the data entity.</param>
/// <param name = "_dataSourceCtx">The context for the data source.</param>
public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(HcmPositionForecastEntity, HcmPositionForecastScenario))
    {
        HcmPositionForecastScenario hcmPositionForecastScenario = _dataSourceCtx.getBuffer();

        //if the Compensation Group has changed, run its defaulting logic.
        if (hcmPositionForecastScenario.isFieldModified(fieldNum(HcmPositionForecastScenario, CompensationGroupId)))
        {
            CompanyInfo legalEntity = CompanyInfo::find(false, hcmPositionForecastScenario.LegalEntity);
            HcmPositionForecastCompensationGroup positionForecastCompensationGroup = HcmPositionForecastCompensationGroup::findByCompensationGrpIdDataAreaId(hcmPositionForecastScenario.CompensationGroupId, legalEntity.DataArea);

            if (HcmPositionForecastFormUtility::handleCompensationGroupModified(hcmPositionForecastScenario.RecId,
                                                                            positionForecastCompensationGroup.CompensationGroupId,
                                                                            legalEntity.DataArea))
            {
                if (positionForecastCompensationGroup.RecId != 0)
                {
                    hcmPositionForecastScenario.CompensationGroupDataAreaId = positionForecastCompensationGroup.DataAreaId;
                    hcmPositionForecastScenario.modifiedField(fieldNum(HcmPositionForecastScenario, CompensationGroupDataAreaId));
                }
                hcmPositionForecastScenario.modifiedField(fieldNum(HcmPositionForecastScenario, CompensationGroupId));
            }
        }
    }
}
