/// </summary>
/// <returns>
/// An expression P&L labor cost
/// </returns>
protected static str createQuery_PlLaborForecastCost(List postingTypeList)
{
    List            transTypeList   = new List(Types::String);

    transTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, ProjTransType)),
                    SysComputedColumn::comparisonLiteral(enum2int(ProjTransType::Hour)))
    );

    transTypeList.addEnd(SysComputedColumn::or(postingTypeList));

    return SysComputedColumn::if(
                SysComputedColumn::and(transTypeList),
                    SysComputedColumn::returnField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, AmountMst)),
                    SysComputedColumn::returnLiteral(0)
    );
}

