/// <summary>
/// Given a list of posting types, creates a computed column for P&L labor Qty
/// </summary>
/// <returns>
/// An expression P&L labor Qty
/// </returns>
protected static str createQuery_PlLaborForecastQty(List postingTypeList)
{
    List PSAIndirectComponentList = new List(Types::String);
    PSAIndirectComponentList.addEnd(
                            SysComputedColumn::equalExpression(
                            SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjCategory) , fieldStr(ProjCategory, PSAIndirectComponent)),
                            SysComputedColumn::comparisonLiteral(enum2int(NoYes::No)))
        );

    List            transTypeList   = new List(Types::String);

    transTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, ProjTransType)),
                    SysComputedColumn::comparisonLiteral(enum2int(ProjTransType::Hour)))
    );

    transTypeList.addEnd(SysComputedColumn::or(postingTypeList));
    transTypeList.addEnd(SysComputedColumn::and(PSAIndirectComponentList));

    return SysComputedColumn::if(
                SysComputedColumn::and(transTypeList),
                    SysComputedColumn::returnField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, Qty)),
                    SysComputedColumn::returnLiteral(0)
    );
}

