    transTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, ProjTransType)),
                    SysComputedColumn::comparisonLiteral(enum2int(ProjTransType::Cost)))
    );

    postingTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, PostingType)),
                    SysComputedColumn::comparisonLiteral(enum2int(LedgerPostingType::ProjAccruedCost)))
    );

    transTypeList.addEnd(SysComputedColumn::and(postingTypeList));

    return SysComputedColumn::if(
