/// </summary>
/// <returns>
/// An expression for WIP Total Cost
/// </returns>
public static str WIPForecastTotalCost()
{
    return SysComputedColumn::add(
                SysComputedColumn::add(PSAForecastEntity::wipLaborForecastCost(), PSAForecastEntity::wipExpenseForecastCost()),
                SysComputedColumn::add(PSAForecastEntity::wipMaterialForecastCost(), PSAForecastEntity::wipCostForecastAccruedLoss())
    );
}

/// <summary>
/// Computed column for labor consumption
/// </summary>
/// <returns>
/// An expression for labor consumption
/// </returns>
public static str laborConsumptionForecast()
{
    List          postingTypeList           = new List(Types::String);

    postingTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, PostingType)),
                    SysComputedColumn::comparisonLiteral(enum2int(LedgerPostingType::ProjCost)))
    );

    postingTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, PostingType)),
                    SysComputedColumn::comparisonLiteral(enum2int(LedgerPostingType::ProjWIPCostvalue)))
    );

    postingTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, PostingType)),
                    SysComputedColumn::comparisonLiteral(enum2int(LedgerPostingType::ProjNeverLedger)))
    );

    postingTypeList.addEnd(
                SysComputedColumn::equalExpression(
                    SysComputedColumn::comparisonField(tableStr(PSAForecastEntity), identifierStr(ProjTransBudget) , fieldStr(ProjTransBudget, PostingType)),
                    SysComputedColumn::comparisonLiteral(enum2int(LedgerPostingType::ProjNoLedger)))
    );

    return PSAForecastEntity::createQuery_LaborConsumptionForecast(postingTypeList);
}

