/// <summary>
/// Computed column for Accrued Revenue Sales Total - Salesvalue + Production + Profit + Subscription
/// </summary>
/// <returns>
/// An expression for Accrued Revenue Sales Total - Salesvalue + Production + Profit + Subscription
/// </returns>
public static str accruedRevenueTotal()
{
    return SysComputedColumn::add(
            SysComputedColumn::add(
                SysComputedColumn::add(PSAForecastEntity::PLTotalAccruedRevenueSalesValue(), PSAForecastEntity::accruedRevenueOnAccount()),
                SysComputedColumn::add(PSAForecastEntity::accruedRevenueProductionTotal(), PSAForecastEntity::accruedRevenueProfitTotal())),
            PSAForecastEntity::accruedRevenueSubscription()
    );
}

