/// </summary>
/// <returns>
/// An expression WIP production value for Labor
/// </returns>
public static str wipRevenueProductionLabor()
{
    List            transTypeList   = new List(Types::String);
    List            postingTypeList = new List(Types::String);

    transTypeList.addEnd(
