Method.Source:
/// <summary>
/// Returns the table extension used for defaulting.
/// </summary>
/// <returns>An instance of the <c>SysTableExtension</c> class.</returns>
public TableExtension getExtension()
{
    return SysTableExtension::construct();
}

public boolean validateWrite()
{
    ProdTable prodTable = ProdTable::find(this.BatchOrderNumber);

    ProdStatus productionStatus = prodTable.ProdStatus;
    if (productionStatus != ProdStatus::Created)
    {
        return checkFailed(strFmt("@SCM:ProdProductionOrderHeaderEntityUpdateNotAllowed", this.BatchOrderNumber, productionStatus, ProdStatus::Created));
    }

    return super();
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM):
            switch (_entityCtx.getDatabaseOperation())
            {
                case DataEntityDatabaseOperation::Insert:
                case DataEntityDatabaseOperation::Update:
                    ProdBOM prodBOM = _dataSourceCtx.getBuffer() as ProdBOM;
                    this.setDefaultDimensions(prodBOM);

                    // will cause the defaulting on the ProdBOM not to call the defaultField for InventDimId.
                    prodBOM.InventDimId = this.InventInventoryDimensionEntityFieldsMapping::resolveInventDimId();

                    // Ensure that default logic on ProdBOM is called for InventDimId.
                    prodBOM.fieldState(fieldNum(ProdBOM, InventDimId), FieldState::NotSet);

                    break;
            }
            break;
    }
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM):
            ProdBOM prodBOM = _dataSourceCtx.getBuffer() as ProdBOM;
            prodBOM.ProdId = this.BatchOrderNumber;
            prodBOM.ItemId = this.ItemNumber;

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert)
            {
                prodBOM.initFromProdTable(this.getExtensionProdTable());
                prodBOM.initFromInventTable(this.getExtensionInventTable());
            }

            // Push inventory dimension from entity to table.
            prodBOM.InventDimId = this.InventInventoryDimensionEntityFieldsMapping::getMergedInventDimId(prodBOM.inventdim());

            // Get inventory dimension from table back to entity.
            this.InventInventoryDimensionEntityFieldsMapping::initFromInventDim(prodBOM.inventDim());
            break;
    }
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM):
            ProdBOM prodBOM = _dataSourceCtx.getBuffer() as ProdBOM;
            this.setQuantityFields(prodBOM);
            break;
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, InventDim):
            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    if (ret)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM))
        {
            ProdBOM prodBOM =  _dataSourceCtx.getBuffer() as ProdBOM;

            // Need to re-apply inventory dimensions as defaulting logic may have changed them.
            this.InventInventoryDimensionEntityFieldsMapping::initFromInventDim(prodBOM.inventDim());
        }
    }

    return ret;
}

private void setQuantityFields(ProdBOM _prodBOM)
{
    if(this.isFieldSet(fieldNum(ProdBatchOrderFormulaLineEntity, FormulaLineQuantity)))
    {
        InventMovement::setMinMaxQty(_prodBOM);
        this.FormulaLineQuantity = _prodBOM.BOMQty;
    }

    if(this.isFieldSet(fieldNum(ProdBatchOrderFormulaLineEntity, EstimatedInventoryQuantity)))
    {
        this.EstimatedInventoryQuantity = _prodBOM.QtyInventCalc;
        InventMovement::bufferSetTransQty(_prodBOM);
    }

    if(this.isFieldSet(fieldNum(ProdBatchOrderFormulaLineEntity, EstimatedFormulaLineQuantity)))
    {
        this.EstimatedFormulaLineQuantity = _prodBOM.QtyBOMCalc;
        InventMovement::setMinMaxQty(_prodBOM);
        InventMovement::bufferSetTransQtyUnit(_prodBOM);
    }

    InventMovement::bufferSetRemainQty(_prodBOM);
}

