{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM):
            ProdBOM prodBOM = _dataSourceCtx.getBuffer() as ProdBOM;
            this.setQuantityFields(prodBOM);
            break;

        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, InventDim):
            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    if (ret)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM))
        {
            ProdBOM prodBOM =  _dataSourceCtx.getBuffer() as ProdBOM;

            // Need to re-apply inventory dimensions as defaulting logic may have changed them.
            this.InventInventoryDimensionEntityFieldsMapping::initFromInventDim(prodBOM.inventDim());
        }
    }

    return ret;
}

public static Query defaultCTQuery()
{
    Query productionBOMQuery = new Query();

    var prodBOMDS = productionBOMQuery.addDataSource(tableNum(ProdBOM));

    // InventDim should not change.        
    var defaultDimensionDS = prodBOMDS.addDataSource(tableNum(DimensionAttributeValueSet));
    defaultDimensionDS.addLink(fieldNum(ProdBOM, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));
    defaultDimensionDS.joinMode(JoinMode::OuterJoin);
    defaultDimensionDS.fetchMode(QueryFetchMode::One2One);

    return productionBOMQuery;
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, InventDim):
            var prodBOMContext = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(ProdBatchOrderFormulaLineEntity, ProdBOM));
            ProdBOM prodBOM = prodBOMContext.getBuffer() as ProdBOM;

            ret = InventDim::find(prodBOM.InventDimId);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

private static str referenceNumberByType(InventRefType _referenceType)
{
    return SysComputedColumn::if(
        SysComputedColumn::EqualExpression(
            SysComputedColumn::comparisonField(tableStr(ProdBatchOrderFormulaLineEntity), identifierStr(ProdBOM), fieldStr(ProdBOM, InventRefType)),
            SysComputedColumn::comparisonLiteral(_referenceType)),
        SysComputedColumn::returnField(tableStr(ProdBatchOrderFormulaLineEntity), identifierStr(ProdBOM), fieldStr(ProdBOM, InventRefId)),
        SysComputedColumn::returnLiteral(''));
}

private static str referenceLotIdByType(InventRefType _referenceType)
{
    return SysComputedColumn::if(
        SysComputedColumn::EqualExpression(
            SysComputedColumn::comparisonField(tableStr(ProdBatchOrderFormulaLineEntity), identifierStr(ProdBOM), fieldStr(ProdBOM, InventRefType)),
            SysComputedColumn::comparisonLiteral(_referenceType)),
        SysComputedColumn::returnField(tableStr(ProdBatchOrderFormulaLineEntity), identifierStr(ProdBOM), fieldStr(ProdBOM, InventRefTransId)),
        SysComputedColumn::returnLiteral(''));
}
