public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdBatchOrderHeaderEntity, InventDim):
            var prodTableContext = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(ProdBatchOrderHeaderEntity, ProdTable));
            ProdTable prodTable = prodTableContext.getBuffer() as ProdTable;

            ret = InventDim::find(prodTable.InventDimId);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;
    if (this.RecId)
    {
        ProdTable prodTable = ProdTable::find(this.BatchOrderNumber);

        ProdStatus productionStatus = prodTable.ProdStatus;

        if (productionStatus != ProdStatus::Created)
        {
            ret = checkFailed(strFmt("@SCM:ProdBatchOrderHeaderEntityUpdateNotAllowed", this.BatchOrderNumber, productionStatus, ProdStatus::Created));
        }

        if (this.SkipCreateBatchOrderFormulaLines && this.inventDimChanged() && !ProdTable::validateBOMId(this.SourceFormulaId, this.ItemNumber, this.getDefaultInventDim()))
        {
            ret = checkFailed(strFmt("@SCM:ProdBatchFormulaIdValidWarning", this.BatchOrderNumber));
        }

        if (this.SkipCreateBatchOrderRouteOperations && this.inventDimChanged() && !ProdTable::validateRouteId(this.SourceRouteId, this.ItemNumber, this.getDefaultInventDim()))
        {
            ret = checkFailed(strFmt("@SCM:ProdBatchRouteIdValidWarning", this.BatchOrderNumber));
        }
    }

    return ret && super();
}

/// <summary>
/// Populates default values in a field.
/// </summary>
/// <param name = "_fieldId">The Id of the field that gets defaulted.</param>
public void defaultField(FieldId _fieldId)
{
    super(_fieldId);

    this.ProdTableEntityMap::defaultField(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(ProdBatchOrderHeaderEntity, PlannedReceiptWarehouseId):
            this.defaultPlannedReceiptWarehouseId();
            break;
        case fieldnum(ProdBatchOrderHeaderEntity, ProductionSiteId):
            this.defaultProductionSiteId();
            break;
        case fieldnum(ProdBatchOrderHeaderEntity, YieldPercentage):
            this.defaultYieldPercentage();
            break;
    }
}

