protected void setSkipCreateFields(NoYes _skip)
{
    if(!this.isfieldSet(fieldNum(ProdBatchOrderHeaderEntity, SkipCreateBatchOrderFormulaLines)))
    {
        this.SkipCreateBatchOrderFormulaLines = _skip;
    }

    if(!this.isfieldSet(fieldNum(ProdBatchOrderHeaderEntity, SkipCreateBatchOrderRouteOperations)))
    {
        this.SkipCreateBatchOrderRouteOperations = _skip;
    }
}

protected void updateScheduleQuantity(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdBatchOrderHeaderEntity, ProdTable))
    {
        ProdTable prodTable = _dataSourceCtx.getBuffer() as ProdTable;

        if(this.ItemNumber)
        {
            if (PdsGlobal::pdsIsCWItem(this.ItemNumber))
            {
                if (this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ScheduledCatchWeightQuantity)))
                {
                    prodTable.QtySched = PdsCatchWeight::inventQty(this.ItemNumber, this.ScheduledCatchWeightQuantity, '');
                    prodTable.PdsCWBatchSched = this.ScheduledCatchWeightQuantity;
                }
            }
            else
            {
                if (this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ScheduledQuantity)))
                {
                    prodTable.QtySched = decRound(this.ScheduledQuantity, UnitOfMeasure::unitOfMeasureDecimalPrecision(
                        UnitOfMeasure::unitOfMeasureIdBySymbol(InventTableModule::find(this.ItemNumber, ModuleInventPurchSales::Invent).UnitId)));
                }
            }

            this.updateFormulaRouteId(prodTable);
        }

        InventMovement::setMinMaxQty(prodTable, false);
        InventMovement::bufferSetRemainQty(prodTable);
        _dataSourceCtx.setBuffer(prodTable);
    }
}

