private InventDim getDefaultInventDim()
{
    InventTable     inventTable = this.getExtensionInventTable();
    InventDim       inventDim = this.InventInventoryDimensionEntityFieldsMapping::resolveInventDim();
    inventDim.initFromInventTable(inventTable, InventItemOrderSetupType::Invent, inventDim);

    return inventDim;
}

private boolean inventDimChanged()
{
    return (
            (this.ScheduledQuantity != this.orig().ScheduledQuantity)  
         || (this.DeliveryDate != this.orig().DeliveryDate)
         || this.isDistinctProductChanged());
}

    private boolean isDistinctProductChanged()
    {
        return  this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ItemNumber)) ||
                this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ProductConfigurationId)) ||
                this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ProductColorId)) ||
                this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ProductSizeId)) ||
                this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ProductStyleId)) ||
				this.isFieldSet(fieldNum(ProdBatchOrderHeaderEntity, ProductVersionId));
    }

private void defaultPlannedReceiptWarehouseId()
{
    if (this.ItemNumber && this.isDistinctProductChanged())
    {
        this.PlannedReceiptWarehouseId = this.getDefaultInventDim().InventLocationId;
    }
}

private void defaultProductionSiteId()
{
    if (this.ItemNumber && this.isDistinctProductChanged())
    {
        this.ProductionSiteId = this.getDefaultInventDim().InventSiteId;
    }
}

private void defaultYieldPercentage()
{
    if (this.ItemNumber || this.IsReworkBatchOrder)
    {
        this.YieldPercentage = 100;
    }
}

private InventTable getExtensionInventTable()
{
    return InventTable::find(this.ItemNumber);
}

private static str skipCreateBatchFormulaLines()
{
    // The <c>SkipCreateBatchOrderFormulaLines</c> field  is only designed for import scenarios
    // so we will always return 'Yes' for export scenarios to ensure that formula lines will not be created by default
    return SysComputedColumn::returnLiteral(NoYes::Yes);
}

private static str skipCreateBatchRouteOperations()
{
    // The <c>SkipCreateBatchOrderRouteOperations</c> field  is only designed for import scenarios
    // so we will always return 'Yes' for export scenarios to ensure that route operations will not be created by default.
    return SysComputedColumn::returnLiteral(NoYes::Yes);
}

private void updateFormulaRouteId(ProdTable _prodTable)
{
    _prodTable.SkipCreateBOMLines = this.SkipCreateBatchOrderFormulaLines;
    if (this.SkipCreateBatchOrderFormulaLines == NoYes::No &&
        this.inventDimChanged())
    {
        if (this.orig().SourceFormulaId == this.SourceFormulaId)
        {
            this.defaultSourceFormulaId();
        }
        _prodTable.BOMId = this.SourceFormulaId;
    }

    this.initFromBOMVersion(_prodTable);

    _prodTable.SkipCreateRouteOperations = this.SkipCreateBatchOrderRouteOperations;
    if (this.SkipCreateBatchOrderRouteOperations == NoYes::No && 
        this.inventDimChanged())
    {
        if (this.orig().SourceRouteId == this.SourceRouteId)
        {
            this.defaultSourceRouteId();
        }
        _prodTable.RouteId = this.SourceRouteId;
    }
}

private void defaultSourceFormulaId()
{
    if (this.ItemNumber)
    {
        this.SourceFormulaId = this.getExtensionInventTable().bomId(this.SourceFormulaVersionValidityDate, this.ScheduledQuantity, this.getDefaultInventDim());
    }
}

private void defaultSourceRouteId()
{
    if (this.ItemNumber)
    {
        this.SourceRouteId = InventTable::find(this.ItemNumber).routeId(this.SourceFormulaVersionValidityDate, this.ScheduledQuantity, this.getDefaultInventDim());
    }
}

private void initFromBOMVersion(ProdTable _prodTable)
{
    if(this.SourceFormulaId)
    {
        BOMVersion bomVersion;
        select firstonly bomVersion
                    order by FromQty desc
                    where bomVersion.ItemId     == this.ItemNumber
                        && bomVersion.Active    == true
                        && bomVersion.BOMId     == this.SourceFormulaId
                        && bomVersion.FromDate  <= this.DeliveryDate
                        && (bomVersion.ToDate   >= this.DeliveryDate
                            || !bomVersion.ToDate)
                        && bomVersion.FromQty   <= this.ScheduledQuantity;
        if (!bomVersion.RecId)
        {
            select firstonly bomVersion
                        order by FromQty asc
                        where bomVersion.ItemId     == this.ItemNumber
                            && bomVersion.Active    == true
                            && bomVersion.BOMId     == this.SourceFormulaId
                            && bomVersion.FromDate  <= this.DeliveryDate
                            && (bomVersion.ToDate   >= this.DeliveryDate
                                || !bomVersion.ToDate);
        }

        if (bomVersion.RecId)
        {
            boolean useBomVersionQty = this.ScheduledQuantity < bomVersion.PmfBatchSize;
            _prodTable.initFromBOMVersion(bomVersion, useBomVersionQty);
            if (useBomVersionQty)
            {
                this.ScheduledQuantity = _prodTable.QtySched;
            }
        }
    }
}

