public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{       
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdProductionOrderHeaderEntity, ProdTable):
            ProdTable prodTable =  _dataSourceCtx.getBuffer() as ProdTable;
            prodTable.ItemId = this.ItemNumber;

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert)
            {
                prodTable.initFromInventTable(this.getExtensionInventTable());

                if(!this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ScheduledQuantity)))
                {
                    this.ScheduledQuantity = prodTable.QtySched;
                    this.RemainingReportAsFinishedQuantity = prodTable.QtySched;
                }

                if(!this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, DeliveryDate)))
                {
                    this.DeliveryDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
                }
            }

            // Push inventory dimension from entity to table
            prodTable.InventDimId = this.InventInventoryDimensionEntityFieldsMapping::getMergedInventDimId(prodTable.inventdim());

            // Get inventory dimension from table back to entity
            this.InventInventoryDimensionEntityFieldsMapping::initFromInventDim(prodTable.inventDim());
            break;
    }        
}

