public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(ProdProductionOrderHeaderEntity, InventDim):
            var prodTableContext = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(ProdProductionOrderHeaderEntity, ProdTable));
            ProdTable prodTable = prodTableContext.getBuffer() as ProdTable;

            ret = InventDim::find(prodTable.InventDimId);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

/// <summary>
///     Validates if a production order has a valid status
/// </summary>
/// <returns>true, if a production order has a valid status; otherwise, false.</returns>
protected boolean validateProductionStatus()
{
    boolean ret = true;
    ProdStatus productionStatus = ProdTable::find(this.ProductionOrderNumber).ProdStatus;

    if (productionStatus != ProdStatus::Created)
    {
        ret = this.validateDeliveryDateUpdate(productionStatus);
    }
    return ret;
}

/// <summary>
///     Validates if the Delivery date field can be updated.
/// </summary>
/// <returns>True if validation is successful; otherwise, false.</returns>
private boolean validateDeliveryDateUpdate(ProdStatus _productionStatus)
{
    boolean onlyDeliveryDateModified;
    boolean ret = true;

    if (this.DeliveryDate != this.orig().DeliveryDate)
    {
        var comparer = VersioningCompareRecordVersions::newTableId(tableNum(ProdProductionOrderHeaderEntity));
        container changes = comparer.packChangedFields(VersioningChangeType::Updated, this, this.orig());

        onlyDeliveryDateModified = conLen(changes) == 1;
    }

    if (onlyDeliveryDateModified)
    {
        ProdStatusType prodStatus = ProdTable::find(this.ProductionOrderNumber).statusClient();

        if (!prodStatus.formFieldsActive())
        {
            ret = checkFailed(strFmt("@SCM:ProdProductionOrderHeaderEntityUpdateDlvDateNotAllowed", this.ProductionOrderNumber, this.ProductionOrderStatus));
        }
    }
    else
    {
        ret = checkFailed(strFmt("@SCM:ProdProductionOrderHeaderEntityUpdateNotAllowed", this.ProductionOrderNumber, _productionStatus, ProdStatus::Created));
    }

    return ret;
}

/// <summary>
///     Validates if a production order has to skip creating production BOM lines
/// </summary>
/// <returns>true, if a production order has to create production BOM lines; otherwise, false.</returns>
protected boolean validateSkipCreateProductionBOMLines()
{
    boolean ret = true;

    if (this.SkipCreateProductionBOMLines && this.inventDimChanged() && !ProdTable::validateBOMId(this.SourceBOMId, this.ItemNumber, this.getDefaultInventDim()))
    {
        ret = checkFailed(strFmt("@SCM:ProdProductionBOMIdValidWarning", this.ProductionOrderNumber));
    }
    return ret;
}

/// <summary>
///     Validates if a production order has to skip creating production route operations
/// </summary>
/// <returns>true, if a production order has to create production route operations; otherwise, false.</returns>
protected boolean validateSkipCreateProductionRouteOperations()
{
    boolean ret = true;

    if (this.SkipCreateProductionRouteOperations && this.inventDimChanged() && !ProdTable::validateRouteId(this.SourceRouteId, this.ItemNumber, this.getDefaultInventDim()))
    {
        ret = checkFailed(strFmt("@SCM:ProdProductionRouteIdValidWarning", this.ProductionOrderNumber));
    }
    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;
    if (this.RecId)
    {
        ret = this.validateProductionStatus()
           && this.validateSkipCreateProductionBOMLines()
           && this.validateSkipCreateProductionRouteOperations();
    }

    return ret && super();
}

/// <summary>
/// Populates default values in a field.
/// </summary>
/// <param name = "_fieldId">The Id of the field that gets defaulted.</param>
public void defaultField(FieldId _fieldId)
{
    super(_fieldId);

    this.ProdTableEntityMap::defaultField(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(ProdProductionOrderHeaderEntity, ProductionWarehouseId):
            this.defaultProductionWarehouseId();
            break;
        case fieldnum(ProdProductionOrderHeaderEntity, ProductionSiteId):
            this.defaultProductionSiteId();
            break;
    }
}

