private InventDim getDefaultInventDim()
{
    InventTable     inventTable = this.getExtensionInventTable();
    InventDim       inventDim = this.InventInventoryDimensionEntityFieldsMapping::resolveInventDim();
    inventDim.initFromInventTable(inventTable, InventItemOrderSetupType::Invent, inventDim);

    return inventDim;
}

private boolean inventDimChanged()
{
    return (
            (this.ScheduledQuantity != this.orig().ScheduledQuantity)  
         || (this.DeliveryDate != this.orig().DeliveryDate)
         || this.isDistinctProductChanged());
}

    private boolean isDistinctProductChanged()
    {
        return  this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ItemNumber)) ||
                this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ProductConfigurationId)) ||
                this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ProductColorId)) ||
                this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ProductSizeId)) ||
                this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ProductStyleId)) ||
				this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ProductVersionId));
    }

private void defaultProductionWarehouseId()
{
    if (this.ItemNumber && this.isDistinctProductChanged())
    {
        this.ProductionWarehouseId = this.getDefaultInventDim().InventLocationId;
    }
}

private void defaultProductionSiteId()
{
    if (this.ItemNumber && this.isDistinctProductChanged())
    {
        this.ProductionSiteId = this.getDefaultInventDim().InventSiteId;
    }
}

private InventTable getExtensionInventTable()
{
    return InventTable::find(this.ItemNumber);
}

private static str skipCreateProductionBOMLines()
{
    // The <c>SkipCreateProductionBOMLines</c> field  is only designed for import scenarios
    // so we will always return 'Yes' for export scenarios to ensure that BOM lines will not be created by default
    return SysComputedColumn::returnLiteral(NoYes::Yes);
}

private static str skipCreateProductionRouteOperations()
{
    // The <c>SkipCreateProductionRouteOperations</c> field  is only designed for import scenarios
    // so we will always return 'Yes' for export scenarios to ensure that route operations will not be created by default.
    return SysComputedColumn::returnLiteral(NoYes::Yes);
}

private void updateBOMRouteId(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdProductionOrderHeaderEntity, ProdTable))
    {
        ProdTable prodTable = _dataSourceCtx.getBuffer() as ProdTable;

        prodTable.SkipCreateBOMLines = this.SkipCreateProductionBOMLines;
        if (this.SkipCreateProductionBOMLines == NoYes::No && 
            this.inventDimChanged())
        {
            if (this.orig().SourceBOMId == this.SourceBOMId)
            {
                this.defaultSourceBOMId();
            }
            prodTable.BOMId = this.SourceBOMId;
        }

        prodTable.SkipCreateRouteOperations = this.SkipCreateProductionRouteOperations;
        if (this.SkipCreateProductionRouteOperations == NoYes::No && 
            this.inventDimChanged())
        {
            if (this.orig().SourceRouteId == this.SourceRouteId)
            {
                this.defaultSourceRouteId();
            }
            prodTable.RouteId = this.SourceRouteId;
        }            
    }
}

protected void updateScheduleQuantity(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdProductionOrderHeaderEntity, ProdTable))
    {
        ProdTable prodTable = _dataSourceCtx.getBuffer() as ProdTable;

        if(this.ItemNumber && this.isFieldSet(fieldNum(ProdProductionOrderHeaderEntity, ScheduledQuantity)))
        {
            this.ScheduledQuantity = decRound(this.ScheduledQuantity, UnitOfMeasure::unitOfMeasureDecimalPrecision(
                    UnitOfMeasure::unitOfMeasureIdBySymbol(InventTableModule::find(this.ItemNumber, ModuleInventPurchSales::Invent).UnitId)));
        }
        this.updateBOMRouteId(_dataSourceCtx);
        InventMovement::bufferSetRemainQty(prodTable);
    }
}

private void defaultSourceBOMId()
{
    if (this.ItemNumber)
    {
        this.SourceBOMId = this.getExtensionInventTable().bomId(this.SourceBOMVersionValidityDate, this.ScheduledQuantity, this.getDefaultInventDim());
    }
}

private void defaultSourceRouteId()
{
    if (this.ItemNumber)
    {
        this.SourceRouteId = InventTable::find(this.ItemNumber).routeId(this.SourceBOMVersionValidityDate, this.ScheduledQuantity, this.getDefaultInventDim());
    }
}

protected void setSkipCreateFields(NoYes _skip)
{
    if(!this.isfieldSet(fieldNum(ProdProductionOrderHeaderEntity, SkipCreateProductionBOMLines)))
    {
        this.SkipCreateProductionBOMLines = _skip;
    }

    if(!this.isfieldSet(fieldNum(ProdProductionOrderHeaderEntity, SkipCreateProductionRouteOperations)))
    {
        this.SkipCreateProductionRouteOperations = _skip;
    }
}

