public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_dataSourceCtx.name() == dataEntityDataSourceStr(ProdSiteSpecificProductionControlParametersEntity, ProdParametersDim))
    {
        this.mapSiteToInventDimId(_dataSourceCtx);
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(ProdSiteSpecificProductionControlParametersEntity, ProdParametersDim))
    {
        this.updateProdStatusParameters();
    }

    return ret;
}

